    var should = require('should');
    var Structure = require('../lib/structure.js')
    const cache = require('../lib/cache')

    describe('supplierTest', function () {

        describe('Search Page', function () {

            var payload = {
                "pageType": "search",
                "url": "https://www.tesco.com/groceries/en-GB/shop/home-and-ents/toys-and-games/all?config=default",
                "html": cache.load("https://www.tesco.com/groceries/en-GB/shop/home-and-ents/toys-and-games/all?config=default")
            }
            var x = 24
            it('should return' + x + ' number of products', function () {

                const searchPage = Structure(payload)
              //  console.log(searchPage)
                searchPage.length.should.equal(x)


                var first = searchPage.shift()
                first.name.should.be.equal("Playdoh Single Tubs");
                first.price.should.be.equal("1.00");
                first.url.should.be.equal("https://www.tesco.com/groceries/en-GB/products/255444781");
                first.image.should.be.equal("https://img.tesco.com/Groceries/pi/324/5010994966324/IDShot_225x225.jpg")
                first.country.should.be.equal('uk');

                var last = searchPage.pop()

                last.name.should.be.equal("Paw Patrol Mashems");
                last.price.should.be.equal("3.00");
                last.url.should.be.equal("https://www.tesco.com/groceries/en-GB/products/290598015");
                last.image.should.be.equal("https://img.tesco.com/Groceries/pi/604/0673534514604/IDShot_225x225.jpg")
                last.country.should.be.equal('uk');


            });

        });

        describe('In stock Page', function () {

            var payload = {
                "pageType": "product",
                "url": "https://www.tesco.com/groceries/en-GB/products/255444781",
                "html": cache.load("https://www.tesco.com/groceries/en-GB/products/255444781")
            }

            it('should be true when product is in stock', function () {

                const inStockPage = Structure(payload)
                 console.log(inStockPage)
                inStockPage.name.should.be.equal("Playdoh Single Tubs");
                inStockPage.price.should.be.equal("1.00");
                inStockPage.url.should.be.equal(payload.url);
                inStockPage.inStock.should.be.equal(true);
                should(inStockPage.stockLevel).be.equal(null);
                inStockPage.inStock.should.be.equal(true);
                inStockPage.image.should.be.equal('https://img.tesco.com/Groceries/pi/324/5010994966324/IDShot_540x540.jpg');
                inStockPage.country.should.be.equal('uk');
            });

        });

        describe('Out Of Stock Page', function () {

            var payload = {
                "pageType": "product",
                "url": "https://www.thebodyshop.com/en-gb/favourites-old/top-rated/twin-ball-massager/p/p000166",
                "html": cache.load("https://www.thebodyshop.com/en-gb/favourites-old/top-rated/twin-ball-massager/p/p000166")
            }

            it('should return false when product is out of stock', function () {

                const inStockPage = Structure(payload)
                inStockPage.name.should.be.equal("Twin-Ball Massager");
                inStockPage.price.should.be.equal('7.00');
                inStockPage.url.should.be.equal(payload.url);
            //    inStockPage.inStock.should.be.equal(false);
                should(inStockPage.stockLevel).be.equal(null);
                inStockPage.image.should.be.equal('https://assets.thebodyshop.com/medias/tbs-logo.svg?context=content-images/h4d/h7c/8796377514014/tbs-logo.svg');
                inStockPage.country.should.be.equal('uk');
            });

        });

    });