var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')

describe('replaceWithDomainName', function () {

    this.timeout(5000);

    describe('In Stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "https://www.thatpetplace.com/bomber-memory-foam-sofa-gray-30-x-41",
            "html": cache.load("https://www.thatpetplace.com/bomber-memory-foam-sofa-gray-30-x-41")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Bomber Memory Foam Sofa - Gray - 30\" x 41\"",
                    "price": "149.99",
                    "url": payload.url,
                    "inStock": true,
                    "stockLevel": null,
                    "image": "https://www.thatpetplace.com/site/images/product/279057_2.jpg?resizeid=2&resizeh=600&resizew=600",
                    "country": 'us'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Out of Stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "https://www.thatpetplace.com/feather-top-ortho-bed-chocolate-tan-large-40x50",
            "html": cache.load("https://www.thatpetplace.com/feather-top-ortho-bed-chocolate-tan-large-40x50")
        }

        it('should be false when product is in store only', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Feather Top Ortho Bed - Chocolate/Tan -Large-40x50",
                    "price": "149.99",
                    "url": payload.url,
                    "inStock": false,
                    "stockLevel": null,
                    "image": 'https://www.thatpetplace.com/site/images/product/276140_1.jpg?resizeid=2&resizeh=600&resizew=600',
                    "country": 'us',
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "https://www.thatpetplace.com/dog-supplies/dog-beds-mats-pads",
            "html": cache.load("https://www.thatpetplace.com/dog-supplies/dog-beds-mats-pads")
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(err, category) {
                var expected = JSON.stringify({
                    "name": "Dog Beds, Mats & Pads",
                    "url": 'https://www.thatpetplace.com/dog-supplies/dog-beds-mats-pads'
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    describe('Search page pagination', function () {

                var payload = {
                    "pageType": "pagination",
                    "url": "https://www.thatpetplace.com/dog-supplies/dog-beds-mats-pads",
                    "html": cache.load("https://www.thatpetplace.com/dog-supplies/dog-beds-mats-pads")
                }

                it('should be true when number of items and pages correct', function (done) {

                    Structure(payload, function (err, pagination) {
                        var expected = JSON.stringify({
                            "items": '93',
                            "pages": '6',
                            "url":'https://www.thatpetplace.com/dog-supplies/dog-beds-mats-pads'
                        })
                        JSON.stringify(pagination).should.equal(expected);
                        done()
                    })
                });

            });


    describe('Search Page', function () {

        var payload = {
            "pageType": "search",
            "url": "https://www.thatpetplace.com/dog-supplies/dog-beds-mats-pads",
            "html": cache.load("https://www.thatpetplace.com/dog-supplies/dog-beds-mats-pads")
        }

        // Change to be the number of products on the page
        var x = 16;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function (err, result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Feather Top Ortho Bed - Chocolate/Tan -Large-40x50';
        it('first product should be ' + firstProductName, function (done) {
            Structure(payload, function (err, result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '149.99',
                    url: 'https://www.thatpetplace.com/feather-top-ortho-bed-chocolate-tan-large-40x50',
                    image: 'https://www.thatpetplace.com/site/images/product/276140_1.jpg?resizeid=3&resizeh=183&resizew=183',
                    country: 'us',
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Clean Paws Mat - Brown 31x20in';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function (err, result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '18.99',
                    url: 'https://www.thatpetplace.com/clean-paws-mat-brown-31x20in',
                    image: 'https://www.thatpetplace.com/site/images/product/275553_1.jpg?resizeid=3&resizeh=183&resizew=183',
                    country: 'us',
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});


