var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')

describe('replaceWithDomainName', function () {

    this.timeout(5000);

    xdescribe('In Stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "",
            "html": cache.load("")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "",
                    "price": "",
                    "url": payload.url,
                    "inStock": true,
                    "stockLevel": null,
                    "image": "",
                    "country": '',
                    "description": " "
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Out of Stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "https://www.thegamery.co.uk/index.php/toys/nerf/nerf-11517-elite-vest-one-size-new.html",
            "html": cache.load("https://www.thegamery.co.uk/index.php/toys/nerf/nerf-11517-elite-vest-one-size-new.html")
        }

        it('should be false when product is in store only', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "NERF 11517 Elite Vest, One Size (New)",
                    "url": payload.url,
                    "inStock": false,
                    "stockLevel": null,
                    "image": 'https://www.thegamery.co.uk/skin/frontend/default/theme290k/images/gamery_logo.png',
                    "country": 'uk'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    xdescribe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "",
            "html": cache.load("")
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(err, category) {
                var expected = JSON.stringify({
                    "name": ""
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    xdescribe('Search page pagination', function () {

                var payload = {
                    "pageType": "pagination",
                    "url": "",
                    "html": cache.load("")
                }

                it('should be true when number of items and pages correct', function (done) {

                    Structure(payload, function (err, pagination) {
                        var expected = JSON.stringify({
                            "items": '',
                            "pages": ''
                        })
                        JSON.stringify(pagination).should.equal(expected);
                        done()
                    })
                });

            });


    describe('Search Page', function () {

        var payload = {
            "pageType": "search",
            "url": "https://www.thegamery.co.uk/index.php/toys/nerf.html?p=2",
            "html": cache.load("https://www.thegamery.co.uk/index.php/toys/nerf.html?p=2")
        }

        // Change to be the number of products on the page
        var x = 9;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function (err, result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'NERF - NITRO 3 PACK - NERF - N';
        it('first product should be ' + firstProductName, function (done) {
            Structure(payload, function (err, result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '10.30',
                    url: 'https://www.thegamery.co.uk/index.php/toys/nerf/nerf-nitro-3-pack-nerf-n-new.html',
                    image: 'https://www.thegamery.co.uk/media/catalog/product/cache/1/small_image/270x/9df78eab33525d08d6e5fb8d27136e95/5/1/51szl7mt6nl.jpg',
                    country: 'uk'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Nerf Ast Rival Zeus MXV 1200 Blaster (Color may vary)';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function (err, result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '69.89',
                    url: 'https://www.thegamery.co.uk/index.php/toys/nerf/nerf-ast-rival-zeus-mxv-1200-blaster-color-may-vary-new.html',
                    image: 'https://www.thegamery.co.uk/media/catalog/product/cache/1/small_image/270x/9df78eab33525d08d6e5fb8d27136e95/5/1/51jcjusw5il.jpg',
                    country: 'uk'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});


