var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')

describe('www.thehut.com', function() {

    before(function()  {
        this.timeout(5000);
    })

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.thehut.com/toys-lego/lego-technic-mining-truck-42035/11020612.html",
            "html" : cache.load("http://www.thehut.com/toys-lego/lego-technic-mining-truck-42035/11020612.html")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(err,inStockPage) {
                var expected = JSON.stringify({
                    "name": "LEGO Technic: Mining Truck (42035)",
                    "price": "24.99",
                    "url": payload.url,
                    "inStock": true,
                    "stockLevel": null,
                    "image": "http://s4.thcdn.com/productimg/0/600/600/12/11020612-1420824268-798530.jpg",
                    "country": "us"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Out of Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.thehut.com/kitchen-accessories/keith-brymer-jones-utensils-pot-white/11176314.html?switchcurrency=USD&shippingcountry=US&affil=awin&awc=2547_1474904021_4b22acb026da3cda4d345b296402b8bc&utm_source=AWin-216555&utm_medium=affiliate&utm_campaign=AffiliateWin",
            "html" : cache.load("http://www.thehut.com/kitchen-accessories/keith-brymer-jones-utensils-pot-white/11176314.html?switchcurrency=USD&shippingcountry=US&affil=awin&awc=2547_1474904021_4b22acb026da3cda4d345b296402b8bc&utm_source=AWin-216555&utm_medium=affiliate&utm_campaign=AffiliateWin")
        }

        it('should return false when product is out of stock', function(done) {

            Structure(payload, function(err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Keith Brymer Jones Utensils Pot - White",
                    "price": "20.40",
                    "url": payload.url,
                    "inStock": false,
                    "stockLevel": null,
                    "image": "http://s2.thcdn.com/productimg/600/600/11176314-8694368492200328.jpg",
                    "country": "us"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        })

    })

    describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "http://www.thehut.com/catalogue/toys/shop-by-category/action-figures.list",
            "html" : cache.load("http://www.thehut.com/catalogue/toys/shop-by-category/action-figures.list")
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(err, pagination) {
                var expected = JSON.stringify({
                    "items": 33,
                    "pages": 2,
                    "url" : "http://www.thehut.com/catalogue/toys/shop-by-category/action-figures.list"
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    })

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "http://www.thehut.com/catalogue/toys/shop-by-category/puzzles-and-jigsaws.list",
            "html" : cache.load("http://www.thehut.com/catalogue/toys/shop-by-category/puzzles-and-jigsaws.list")
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(err, category) {
                var expected = JSON.stringify({
                    "name": "Puzzles and Jigsaws",
                    "url" : "http://www.thehut.com/catalogue/toys/shop-by-category/puzzles-and-jigsaws.list"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

     describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "https://www.thehut.com/catalogue/games.list",
            "html" : cache.load("https://www.thehut.com/catalogue/games.list")
        }

        // Change to be the number of products on the page
        var x = 66;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(err, result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Sony PlayStation 4 DualShock 4 Controller V2 - Glacier White';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(err, result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '44.99',
                    url: 'https://www.thehut.com/ps4-accessories/sony-playstation-4-dualshock-4-controller-v2-glacier-white/11022799.html',
                    image: 'https://s2.thcdn.com/productimg/0/300/300/99/11022799-1411992873-986262.jpg',
                    country: 'uk'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Star Wars Battlefront Ultimate Edition';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(err, result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '24.99',
                    url: 'https://www.thehut.com/xbox-one-games/star-wars-battlefront-ultimate-edition/11529460.html',
                    image: 'https://s3.thcdn.com/productimg/300/300/11529460-1414507062104295.jpg',
                    country: 'uk'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });



});



