var should = require('should');
var Structure = require('../lib/structure.js')

describe('replaceWithwebAddress', function() {

    this.timeout(10000);

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "https://www.thenorthface.com/shop/equipment-technical-packs-backpacking/fovero-70-cwt6?variationId=EQC"
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "FOVERO 70",
                    "price": "289.95",
                    "url": null,
                    "inStock": true,
                    "stockLevel": null,
                    "image": 'https://images.thenorthface.com/is/image/TheNorthFace/CWT6_EQC_hero',
                    "country": 'us',
                    "description": "Don't leave anything at home during your next extended stay in the backcountry with this 70-liter technical pack that's designed for exceptional load management and organization of up to 70 pounds of gear."
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    // describe('Out of Stock Page', function() {

    //     var payload = {
    //         "pageType" : "product",
    //         "url" : ""
    //     }

    //     it('should be false when product is in store only', function (done) {

    //         Structure(payload, function(inStockPage) {
    //             var expected = JSON.stringify({
    //                 "name": "",
    //                 "price": "",
    //                 "url": null,
    //                 "inStock": false,
    //                 "stockLevel": null,
    //                 "image": '',
    //                 "country": '',
    //                 "description": ''
    //             })
    //             JSON.stringify(inStockPage).should.equal(expected);
    //             done()
    //         })
    //     });

    // });

    describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "https://www.thenorthface.com/shop/equipment-technical-packs-backpacking"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(pagination) {
                var expected = JSON.stringify({
                    "items": 13
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    })

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "https://www.thenorthface.com/shop/equipment-technical-packs-backpacking"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(category) {
                var expected = JSON.stringify({
                    "name": "Backpacking"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "https://www.thenorthface.com/shop/equipment-technical-packs-backpacking"
        }

        // Change to be the number of products on the page
        var x = 13;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = "FOVERO 70";
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '289.95',
                    url: 'https://www.thenorthface.com/shop/equipment-technical-packs-backpacking/fovero-70-cwt6?variationId=EQC',
                    image: 'https://images.thenorthface.com/is/image/TheNorthFace/CWT6_EQC_hero?$262x306$',
                    country: 'us'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'YOUTH TERRA 55';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '159.00',
                    url: 'https://www.thenorthface.com/shop/equipment-technical-packs-backpacking/youth-terra-55?variationId=FNW',
                    image: 'https://images.thenorthface.com/is/image/TheNorthFace/A1P3_FNW_hero?$262x306$',
                    country: 'us'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});
