var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')

describe('www.thetoyshop.com', function() {

    before(function()  {
        this.timeout(5000);
    })

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.thetoyshop.com/electronic-educational-toys/electronic-learning-toys/e-learning-computers/VTech-Baby%27s-Laptop/p/513024",
            "html": cache.load("http://www.thetoyshop.com/electronic-educational-toys/electronic-learning-toys/e-learning-computers/VTech-Baby%27s-Laptop/p/513024")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "VTech Baby's Laptop",
                    "price": "22.00",
                    "url": payload.url,
                    "inStock": true,
                    "stockLevel": null,
                    "image": "http://www.thetoyshop.com/medias?context=bWFzdGVyfGltYWdlc3w1MjY2NXxpbWFnZS9qcGVnfGltYWdlcy84ODYyNDA1OTUxNTE4LmpwZ3wt",
                    "country": "uk",
                    "sku": "513024"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

     describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "http://www.thetoyshop.com/c/arts-creative-play?q=%3Aprice-desc?AR6%3Aage%3A0097",
            "html": cache.load("http://www.thetoyshop.com/c/arts-creative-play?q=%3Aprice-desc?AR6%3Aage%3A0097")
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(err, pagination) {
                var expected = JSON.stringify({
                    "items": 33,
                    "pages": 14,
                    "url" : payload.url
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    });

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "http://www.thetoyshop.com/c/arts-creative-play?q=%3Aprice-desc?AR6%3Aage%3A0097",
            "html": cache.load("http://www.thetoyshop.com/c/arts-creative-play?q=%3Aprice-desc?AR6%3Aage%3A0097")
        }

        it('should be true when the category is correct', function (done) {

            Structure(payload, function(err, category) {
                var expected = JSON.stringify({
                    "name": "Arts and creative play - 8 - 11 years",
                    "url": payload.url
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "https://www.thetoyshop.com/c/dolls-soft-toys/dolls",
            "html": cache.load("https://www.thetoyshop.com/c/dolls-soft-toys/dolls")
        }

        // Change to be the number of products on the page
        var x = 36;
        it('should return ' + x + ' number of products', function (done) {
            Structure(payload, function(err, result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'JoJo Siwa Style & Play Doll';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(err, result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '15.00',
                    url: 'https://www.thetoyshop.com/dolls-soft-toys/dolls/fashion-dolls-and-accessories/JoJo-Siwa-Style-%26-Play-Doll/p/534727',
                    image: 'https://www.thetoyshop.com/medias/?context=bWFzdGVyfGltYWdlc3w2MDkxfGltYWdlL3BuZ3xpbWFnZXMvaDJiL2gyMS85MTI4NDY5NzI1MjE0LnBuZ3xlMTU3YWQ0ZTJjMGU3NmVjZWIwMjA1YWY4YWQyMGUzNzIwMWUwN2MzN2QyMmI1OWUwNmFkZGM0MGQyNDA1ZTYx',
                    country: 'uk',
                    sku: "534727"
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Sylvanian Families Toy Poodle Twins';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(err, result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '10.00',
                    url: 'https://www.thetoyshop.com/dolls-soft-toys/dolls/animal-figures/Sylvanian-Families-Toy-Poodle-Twins/p/532334',
                    image: 'https://www.thetoyshop.com/medias/532334-Primary-515Wx515H?context=bWFzdGVyfGltYWdlc3wxNjI2MjN8aW1hZ2UvcG5nfGltYWdlcy9oMjcvaDBhLzkxMzIxODU3MTQ3MTgucG5nfGI5OGQ1NmEzMmRjYjE2OWM1NmI0ODQ5ZWFlMzEzYTExNTM3ZWQwMzAzNmQ0Yjk3N2NlOTRjZjdkOGM4MzVmMDY',
                    country: 'uk',
                    sku: "532334"
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});
