    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "http://www.thewatchery.com/results.asp?clr=15&ndd=2&obg=||4|||||||"
        }

        // Change to be the number of products on the page
        var x = 60;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'TechnoMarine Men\'s Manta Chronograph SS and Gold-Tone SS White MOP Dial';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '139.99',
                    url: 'http://www.thewatchery.com/detail.asp?iq=1&bo_products_variance_id=347459&promotion_code=PBC347459&obp=0*&obg=||4|||||||&clr=15&ps=96',
                    image: 'http://www.thewatchery.com/images/L/TECHNO-TM-215041.jpg',
                    country: 'us'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Invicta Men\'s Aviator Automatic Black Silicone Gold-Tone Dial 18K GP SS';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '89.99',
                    url: 'http://www.thewatchery.com/detail.asp?iq=1&bo_products_variance_id=345554&promotion_code=PBC345554&obp=0*&obg=||4|||||||&clr=15&ps=96',
                    image: 'http://www.thewatchery.com/images/L/INVICTA-23532.jpg',
                    country: 'us'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

     describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "http://www.thewatchery.com/results.asp?clr=15&ndd=2&obg=||4|||||||"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(pagination) {
                var expected = JSON.stringify({
                    "items": 60,
                    "pages": 43
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    });
