var should = require('should');
var Structure = require('../lib/structure.js')

describe('www.thewatchhut.co.uk', function() {

    this.timeout(10000);

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.thewatchhut.co.uk/casio/mens-casio-g-shock-gulfmaster-alarm-chronograph-watch-gn-1000b-1aer-gn-1000b-1aer.html"
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "Mens Casio G-Shock Gulfmaster Alarm Chronograph Watch GN-1000B-1AER",
                    "price": "250.00",
                    "url": null,
                    "inStock": true,
                    "stockLevel": null,
                    "image": 'http://cdn1.thewatchhut.co.uk/products/main/99984383/1000x1500/gn-1000b-1aer-1439470701-2879.jpg',
                    "country": 'uk',
                    "description": "This men's Casio G-shock Gulfmaster watch is made from stainless steel and resin and is powered by a chronograph quartz movement. It is fastened with a black rubber strap and has a black dial. The watch has a date function. Includes alarm function. ©"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Out of Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.thewatchhut.co.uk/gc/mens-gc-technoclass-chronograph-watch-x81007g2s-x81007g2s.html"
        }

        it('should be false when product is in store only', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "Mens Gc TechnoClass Chronograph Watch X81007G2S",
                    "price": "247.50",
                    "url": null,
                    "inStock": false,
                    "stockLevel": null,
                    "image": 'http://d7ujeqwvh5yf8.cloudfront.net/products/main/99961559/1000x1500/x81007g2s-1373555686-407.jpg',
                    "country": 'uk',
                    "description": "This attractive gents Gc Technoclass watch is made from two-tone steel/gold plate and is fitted with a chronograph quartz movement. It is fastened with a black leather strap and has a black dial. The watch has a date function. ©"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "http://www.thewatchhut.co.uk/mens-watches.htm?show=96"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(pagination) {
                var expected = JSON.stringify({
                    "items": 96,
                    "pages": 59
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    })

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "http://www.thewatchhut.co.uk/mens-watches.htm?show=96"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(category) {
                var expected = JSON.stringify({
                    "name": "Men's Watches"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "http://www.thewatchhut.co.uk/ladies-watches.htm?price=1to10000"
        }

        // Change to be the number of products on the page
        var x = 48;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Ladies Olivia Burton Brown Dial Watch OB16BL36';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '67.50',
                    url: 'http://www.thewatchhut.co.uk/olivia-burton/ladies-olivia-burton-brown-dial-watch-ob16bl36-ob16bl36.html',
                    image: 'http://twhprd2.cookieless.cloud/products/main/100000068/category/ob16bl36-1475841048-1941.jpg',
                    country: 'uk'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Ladies Olivia Burton Midi Dial Watch OB15MD50';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '80.00',
                    url: 'http://www.thewatchhut.co.uk/olivia-burton/ladies-olivia-burton-midi-dial-watch-ob15md50-ob15md50.html',
                    image: 'http://twhprd2.cookieless.cloud/products/main/99999100/category/ob15md50-1464948449-9367.jpg',
                    country: 'uk'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});
