var should = require('should');
var Structure = require('../lib/structure.js')


describe('www.theworks.co.uk', function() {

    before(function()  {
        this.timeout(5000); 
    })


    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "https://www.theworks.co.uk/c/toys-games/action-figures-playsets/?pageSize=20&page=1"
        }

        it('should return 20 products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(20);
                done()
            })
        });

        it('last product should be Metals Batman V Superman Wonder Woman Collectible Figurine', function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: 'Metals Batman V Superman Wonder Woman Collectible Figurine',
                    price: '10.00',
                    url: 'https://www.theworks.co.uk/p/action-figures-playsets/metals-batman-v-superman-wonder-woman-collectible-figurine/801310976678',
                    image: 'https://static.theworks.co.uk/images/801310976678_M.jpg',
                    country: 'uk'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

        it('first product should Game Of Thrones Figure - Grey Worm', function(done) {
            Structure(payload, function(result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: 'Game Of Thrones Figure - Grey Worm',
                    price: '10.00',
                    url: 'https://www.theworks.co.uk/p/action-figures-playsets/game-of-thrones-figure---grey-worm/761568291445',
                    image: 'https://static.theworks.co.uk/images/761568291445_M.jpg',
                    country: 'uk'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

    });


    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.theworks.co.uk/p/board-games/world-of-tanks-rush-card-game/3558380023227"
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "World Of Tanks Rush Card Game",
                    "price": "5.00",
                    "url": null,
                    "inStock": true,
                    "stockLevel": null,
                    "image": "http://media.theworks.co.uk/images/3558380023227_L.jpg",
                    "country": "uk"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });


    describe('Out of Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.theworks.co.uk/p/strategy-games/origin-game/3760146642140"
        }

        it('should return false when product is out of stock', function(done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "Origin Game",
                    "price": "12.00",
                    "url": null,
                    "inStock": false,
                    "stockLevel": null,
                    "image": "http://media.theworks.co.uk/images/3760146642140_L.jpg",
                    "country": "uk"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        })

    })

      describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "http://www.theworks.co.uk/c/art-supplies?page=1&pageSize=200&orderBy=7"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(pagination) {
                var expected = JSON.stringify({
                    "items": 200,
                    "pages": 2
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });
    });



    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "http://www.theworks.co.uk/c/toys-games/strategy-games"
        }

        it('should be true when the category is correct', function (done) {

            Structure(payload, function(category) {
                var expected = JSON.stringify({
                    "name": "Strategy Games"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

});
