var should = require('should');
var Structure = require('../lib/structure.js')

describe('www.thinkgeek.com', function() {

    this.timeout(10000);

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.thinkgeek.com/product/imvt/"
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "Star Wars Death Star Kitchen Timer",
                    "price": "14.99",
                    "url": null,
                    "inStock": true,
                    "stockLevel": null,
                    "image": "http://www.thinkgeek.com/images/products/frontsquare/imvt_sw_deathstar_kitchen_timer.jpg",
                    "country": "us"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    // describe('Out of Stock Page', function() {

    //     var payload = {
    //         "pageType" : "product",
    //         "url" : "http://www.thinkgeek.com/product/1379/"
    //     }

    //     it('should be false when product is in store only', function (done) {

    //         Structure(payload, function(inStockPage) {
    //             var expected = JSON.stringify({
    //                 "name": "Game of Thrones Wax Seal Coasters",
    //                 "price": "19.99",
    //                 "url": null,
    //                 "inStock": false,
    //                 "stockLevel": null,
    //                 "image": "http://www.thinkgeek.com/images/products/frontsquare/1379_game_of_thrones_wax_seal_coasters.jpg",
    //                 "country": "us"
    //             })
    //             JSON.stringify(inStockPage).should.equal(expected);
    //             done()
    //         })
    //     });

    // });

    describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "http://www.thinkgeek.com/toys-games/feature/desc/0/100/"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(pagination) {
                var expected = JSON.stringify({
                    "items": 100,
                    "pages": 3
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    });

    describe('Check for no pagination (after failing run on server)', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "http://www.thinkgeek.com/product/5bb0/feature/desc/1/100/"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(pagination) {
                var expected = JSON.stringify({
                    "items": null,
                    "pages": null
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    });


    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "http://www.thinkgeek.com/toys-games/feature/desc/0/100/"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(category) {
                var expected = JSON.stringify({
                    "name": "Toys & Games"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "http://www.thinkgeek.com/homeoffice/kitchen/"
        }

        // Change to be the number of products on the page
        var x = 100;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Star Wars Death Star Waffle Maker';
        it('first product should be' + firstProductName, function(done) {
            Structure(payload, function(result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '19.99',
                    url: 'http://www.thinkgeek.com/product/huik/',
                    image: 'http://www.thinkgeek.com/images/products/frontsquare/huik_sw_death_star_waffle_maker.jpg',
                    country: 'us'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Definitely Not Poison Skull Bottle Stopper';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '29.99',
                    url: 'http://www.thinkgeek.com/product/jivi/',
                    image: 'http://www.thinkgeek.com/images/products/frontsquare/jivi_not_poison_skull_bottle_stopper.jpg',
                    country: 'us'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

    describe('Checking to see if no pagination works', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "http://www.thinkgeek.com/help/terms-of-use.shtml"
        }

        it('should return 0 items and null pages', function (done) {

            Structure(payload, function(pagination) {
                var expected = JSON.stringify({
                    "items": null,
                    "pages": null
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    })

});
