    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "http://www.tigerdirect.com/applications/category/category_slc.asp?CatId=4935#"
        }

        // Change to be the number of products on the page
        var x = 10;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'HP Probook 440 G3 Notebook PC – Intel Core i7-6500U Dual-Core Processor, 2.50GHz, 16GB DDR3, 256GB SSD, 14” LED, FHD 1920x1080, USB 2.0, Windows 7 Pro 64-Bit with Windows 10 Pro License - X9U27UT#ABA';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '699.99',
                    url: 'http://www.tigerdirect.com/applications/SearchTools/item-details.asp?EdpNo=492903&CatId=4935',
                    image: 'http://cdn-eu-ec.yottaa.net/56abbca0312e5815f5000542/e42d88e0d50401335179123dfe2baf36.yottaa.net/v~13.2e/skuimages/medium/40113931_vmain01_ss_mn_492903.jpg?yocs=2m_2E_&yoloc=eu',
                    country: 'us'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'HP 17-x037cl Notebook PC - Intel Core i3-5005U Dual-Core Processor, 2.0GHz, 8GB DDR3L, 1TB HDD, 17.3" HD+ WLED (1600x900), HDMI, USB 2.0, USB 3.0 (Refurbished) - W2N00UAR#ABA';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '329.99',
                    url: 'http://www.tigerdirect.com/applications/SearchTools/item-details.asp?EdpNo=4672038&CatId=4935',
                    image: 'http://cdn-eu-ec.yottaa.net/56abbca0312e5815f5000542/e42d88e0d50401335179123dfe2baf36.yottaa.net/v~13.2e/skuimages/medium/40280882_vmain01_DC_mn_4672038.jpg?yocs=2m_2E_&yoloc=eu',
                    country: 'us'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });
     describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "http://www.tigerdirect.com/applications/category/category_slc.asp?CatId=4935#"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(pagination) {
                var expected = JSON.stringify({
                    "items": 10,
                    "pages": 553
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    });

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "http://www.tigerdirect.com/applications/category/category_slc.asp?CatId=4935"
        }

        it('should be true when the category is correct', function (done) {

            Structure(payload, function(category) {
                var expected = JSON.stringify({
                    "name": ""
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });


