    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "https://www.tjhughes.co.uk/small-electricals-c294",
            "html": cache.load("https://www.go2games.com/xbox-360?genre2=2064")
        }

        it('should be true when the category is correct', function (done) {

            Structure(payload, function(category) {
                var expected = JSON.stringify({
                    "name": "Small Electricals"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "https://www.tjhughes.co.uk/gar den-electricals-c1502",
            "html": cache.load("https://www.tjhughes.co.uk/garden-electricals-c1502")
        }

        // Change to be the number of products on the page
        var x = 4;
        it('should return ' + x + ' numer of products', function () {
            let result = Structure(payload)
            result.length.should.equal(x);
        });

        var firstProductName = 'Mains Powered Hedge Trimmer';
        it('first product should be ' + firstProductName, function() {

            var expected = JSON.stringify({
                barcode: null,
                name: firstProductName,
                price: '29.99',
                url: 'https://www.tjhughes.co.uk/mains-powered-hedge-trimmer-p3337',
                image: 'https://tjhughes_co_uk.secure-cdn.visualsoft.co.uk/images/mains-powered-hedge-trimmer-p3337-5364_thumb.jpg',
                country: 'uk'
            })
            
            let result = Structure(payload)

            var first = result.shift()
            
            JSON.stringify(first).should.equal(expected)

        })

        var lastProductName = 'GardenKraft Indoor Insect Killer';
        it('last product should be ' + lastProductName, function () {
            var expected = JSON.stringify({
                barcode: null,
                name: lastProductName,
                price: '7.99',
                url: 'https://www.tjhughes.co.uk/gardenkraft-indoor-insect-killer-p13437',
                image: 'https://tjhughes_co_uk.secure-cdn.visualsoft.co.uk/images/gardenkraft-indoor-insect-killer-p13437-39270_thumb.jpg',
                country: 'uk'
            })

            const result = Structure(payload);

            var last = result.pop()
            JSON.stringify(last).should.equal(expected)
        })

    });
