var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')

describe('www.tkmaxx.com', function () {


    describe('In stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "https://www.tkmaxx.com/men/formal-wear/formal-shoes/brown-leather-city-shoes/p/27027013",
            "html": cache.load("https://www.tkmaxx.com/men/formal-wear/formal-shoes/brown-leather-city-shoes/p/27027013")
        }

        it('should be true when product is in stock', function () {

            const inStockPage =  Structure(payload)
            inStockPage.name.should.be.equal("John White Brown Leather City Shoes");
            inStockPage.price.should.be.equal("49.99");
            inStockPage.url.should.be.equal("https://www.tkmaxx.com/men/formal-wear/formal-shoes/brown-leather-city-shoes/p/27027013");
            inStockPage.inStock.should.be.equal(true);
            should(inStockPage.stockLevel).be.equal(null);
            inStockPage.image.should.be.equal('https://www.tkmaxx.com/medias/27027013-medium-wl-01.jpg?context=bWFzdGVyfHByb2R1Y3QtaW1hZ2VzfDEyOTc5fGltYWdlL2pwZWd8cHJvZHVjdC1pbWFnZXMvaGIxL2hhZC85OTc2NTMzMjIxNDA2LmpwZ3w3NWZkYzJjYWZjYTg5NDFkMTYwYjY5ZmRjYmQ0ZGJlNzZhNTVmYWUzY2Y0ZDVlNzZlMWExZDMxNzEyZTUwNzY1');
            inStockPage.country.should.be.equal('uk');            
 
        });

    });

    describe('Search Page', function () {

        var payload = {
            "pageType": "search",
            "url": "https://www.tkmaxx.com/uk/en/clearance/kids+toys/boys/c/05030300?q=&sort=publishedDate-desc&facets=stockLevelStatus:inStock&page=3",
            "html": cache.load("https://www.tkmaxx.com/uk/en/clearance/kids+toys/boys/c/05030300?q=&sort=publishedDate-desc&facets=stockLevelStatus:inStock&page=3")
        }

        // Change to be the number of products on the page
        var x = 196;
        it('should return ' + x + ' numer of products', function () {
            let result = Structure(payload)
            result.length.should.equal(x);
        });

        var firstProductName = 'Navy Branded T Shirt';
        it('first product should be ' + firstProductName, function() {

            var expected = JSON.stringify({
                barcode: null,
                name: firstProductName,
                price: '6.00',
                url: 'https://www.tkmaxx.com/kids/boys/tops/navy-branded-t-shirt/p/41658703',
                image: 'https://www.tkmaxx.com/medias/41658703-search-wl-01.jpg?context=bWFzdGVyfHByb2R1Y3QtaW1hZ2VzfDcwNTB8aW1hZ2UvanBlZ3xwcm9kdWN0LWltYWdlcy9oYTkvaDQ4Lzk4OTQ4Nzc2NTkxNjYuanBnfDZmMTU1ZWJmM2ZhYjgyNzhhMDJkNTNjNTBlY2FiNjYxMWZiY2VjYWRhZGZkMjIyODBkZDRjYzhjZjJiMjIyOTY',
                country: 'uk'
            })
            
            let result = Structure(payload)

            var first = result.shift()
            
            JSON.stringify(first).should.equal(expected)

        })

        var lastProductName = 'Light Blue Logo Hoodie';
        it('last product should be ' + lastProductName, function () {
            var expected = JSON.stringify({
                barcode: null,
                name: lastProductName,
                price: '11.00',
                url: 'https://www.tkmaxx.com/kids/boys/sweatshirts+hoodies/light-blue-logo-hoodie/p/41643663',
                image: 'https://www.tkmaxx.com/medias/41643663-search-wl-01.jpg?context=bWFzdGVyfHByb2R1Y3QtaW1hZ2VzfDk2Nzd8aW1hZ2UvanBlZ3xwcm9kdWN0LWltYWdlcy9oNjYvaGFlLzk5MzEzMjk5MjkyNDYuanBnfGQ4ZDZiODZmMzkyMmQyMjhmYzZlZTY2NmM2YTM5YjBiNzBkYzk5MzM3MTEyOTk0NjlkZjNmM2M0NDljNTVjNWY',
                country: 'uk'
            })

            const result = Structure(payload);

            var last = result.pop()
            JSON.stringify(last).should.equal(expected)
        })

    });

});