var should = require('should');
var Structure = require('../lib/structure.js')

describe('replaceWithwebAddress', function() {

    this.timeout(10000);

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.tmart.com/T95n-Mini-mx-Amlogic-S905-Quad-Core-Android-5.1-HD-TV-Player-with-1GB-RAM-8GB-ROM-WiFi-4k-kodi-Black_p348694.html"
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "T95n Mini mx+ Amlogic S905 Quad-Core Android 5.1 HD TV Player with 1GB RAM + 8GB ROM/WiFi/4k/kodi Black",
                    "price": "31.02",
                    "url": null,
                    "inStock": true,
                    "stockLevel": null,
                    "image": "http://www.image-tmart.com/prodimgs_v2/8/8802/1801/88021801/T95n-Mini-mx-Amlogic-S905-QuadCore-Android-51-HD-TV-Player-with-1GB-RAM-8GB-ROMWiFi4kkodi-Black_320x320.jpg?1469759474",
                    "country": "uk"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Out of Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.tmart.com/Natural-Latex-100-Feet-Expandable-Flexible-Garden-Hose-with-Spray-Nozzle_p336826.html"
        }

        it('should be false when product is in store only', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "Natural Latex 100 Feet Expandable Flexible Garden Hose with Spray Nozzle",
                    "price": "17.78",
                    "url": null,
                    "inStock": false,
                    "stockLevel": null,
                    "image": "http://www.image-tmart.com/prodimgs_v2/1/1302/5969/13025969/Natural-Latex-100-Feet-Expandable-Flexible-Garden-Hose-with-Spray-Nozzle_320x320.jpg?1448523067",
                    "country": "uk"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });


    describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "http://www.tmart.com/DIY-Parts-Components/"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(pagination) {
                var expected = JSON.stringify({
                    "items": 72,
                    "pages": 2
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    })

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "http://www.tmart.com/DIY-Parts-Components/"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(category) {
                var expected = JSON.stringify({
                    "name": "DIY Parts & Components"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "http://www.tmart.com/Home-Appliances/"
        }

        // Change to be the number of products on the page
        var x = 36;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Water Household Water Purifier Filter Kettle Active Carbon Water Purifier Cup Compact Water Purifier White & Blue';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '23.96',
                    url: 'http://www.tmart.com/Water-Household-Water-Purifier-Filter-Kettle-Active-Carbon-Water-Purifier-Cup-Compact-Water-Purifier-White-Blue_p367048.html',
                    image: 'http://www.image-tmart.com/prodimgs_v2/8/8602/6452/86026452/Water-Household-Water-Purifier-Filter-Kettle-Active-Carbon-Water-Purifier-Cup-Compact-Water-Purifier-White-Blue_180x180.jpg?1500013816',
                    country: 'uk'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Broadlink MR Mini3 Universal Wi-Fi / IR Remote Controller with APP Control / Timing Function for Smart Devices Electric Appliance Black';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '15.97',
                    url: 'http://www.tmart.com/Broadlink-MR-Mini3-Universal-Wi-Fi-IR-Remote-Controller-with-APP-Control-Timing-Function-for-Smart-Devices-Electric-Appliance-Black_p352749.html',
                    image: 'http://www.image-tmart.com/prodimgs_v2/8/8601/1752/86011752/Broadlink-MR-Mini3-Universal-WiFi-IR-Remote-Controller-with-APP-Control-Timing-Function-for-Smart-Devices-Electric-Appliance-Black_180x180.jpg?1476154493',
                    country: 'uk'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});
