var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')

describe('replaceWithwebAddress', function () {

    this.timeout(10000);

    describe('In Stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "http://www.toolbarn.com/milwaukee-48-11-2830.html",
            "html" : cache.load("http://www.toolbarn.com/milwaukee-48-11-2830.html")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Milwaukee 48-11-2830 M28 28 Volt Lithium-Ion Battery",
                    "price": "154.70",
                    "url": "http://www.toolbarn.com/milwaukee-48-11-2830.html",
                    "inStock": "UNCHECKED",
                    "stockLevel": null,
                    "image": 'http://d2blls2j5typlg.cloudfront.net/media/catalog/product/cache/1/image/450x/9df78eab33525d08d6e5fb8d27136e95/8/C/8CC4A4B5-511E-4E72-BCC9-E0D036EB12AF_7.jpg',
                    "country": 'us',
                    "barcode": '045242090907',
                    "sku": 'milwaukee-48-11-2830'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    // describe('Out of Stock Page', function() {

    //     var payload = {
    //         "pageType" : "product",
    //         "url" : ""
    //     }

    //     it('should be false when product is in store only', function (done) {

    //         Structure(payload, function(inStockPage) {
    //             var expected = JSON.stringify({
    //                 "name": "",
    //                 "price": "",
    //                 "url": null,
    //                 "inStock": false,
    //                 "stockLevel": null
    //             })
    //             JSON.stringify(inStockPage).should.equal(expected);
    //             done()
    //         })
    //     });

    // });

    describe('Search Page', function ()  {

        var payload = {
            "pageType": "search",
            "url": "https://www.toolbarn.com/shop-by-category/air-tools/nailers/brad-nailers/",
            "html" : cache.load("https://www.toolbarn.com/shop-by-category/air-tools/nailers/brad-nailers/")
            
        }

        // Change to be the number of products on the page
        var x = 20;
        it('should return ' + x + ' number of products', function (done) {
            Structure(payload, function (err, result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Hitachi 2" 18-Gauge Pnematic Brad Nailer';
        it('first product should be ' + firstProductName, function (done) {
            Structure(payload, function (err, result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '89.00',
                    url: 'https://www.toolbarn.com/hitachi-nt50ae2s.html/',
                    image: 'https://www.toolbarn.com/media/catalog/product/cache/1/small_image/245x/9df78eab33525d08d6e5fb8d27136e95/B/C/BC7BAA86-D3E8-44FD-8BCE-27DA16E730E9_7.jpg',
                    country: 'us',
                    sku: 'hitachi-nt50ae2s'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'DEWALT 18 Gauge 2" Pnematic Brad Nailer Kit';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function (err, result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '79.99',
                    url: 'https://www.toolbarn.com/dewalt-dwfp12231.html/',
                    image: 'https://www.toolbarn.com/media/catalog/product/cache/1/small_image/245x/9df78eab33525d08d6e5fb8d27136e95/d/e/dewalt_dwfp12231_1.jpg',
                    country: 'us',
                    sku: 'dewalt-dwfp12231'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

    describe('Search page pagination', function () {

        var payload = {
            "pageType": "pagination",
            "url": "http://www.toolbarn.com/shop-by-category/accessories/batteries-chargers",
            "html" : cache.load("http://www.toolbarn.com/shop-by-category/accessories/batteries-chargers")
            
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function (err, pagination) {
                var expected = JSON.stringify({
                    "items": 20,
                    "pages": 5,
                    "url":"http://www.toolbarn.com/shop-by-category/accessories/batteries-chargers"
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    });

    describe('Search page category', function () {

        var payload = {
            "pageType": "category",
            "url": "http://www.toolbarn.com/shop-by-category/accessories/batteries-chargers",
            "html" : cache.load("http://www.toolbarn.com/shop-by-category/accessories/batteries-chargers")
        }

        it('should be true when the category is correct', function (done) {

            Structure(payload, function (err, category) {
                var expected = JSON.stringify({
                    "name": "Batteries & Chargers",
                    "url":"http://www.toolbarn.com/shop-by-category/accessories/batteries-chargers"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

});
