var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')

describe('www.toolstation.com', function() {

    this.timeout(10000);


        describe('In stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "https://www.tesco.com/direct/microsoft-xbox-one-x-1tb-4k-console/234-7189.prd?skuId=234-7189",
            "html" : cache.load("https://www.tesco.com/direct/microsoft-xbox-one-x-1tb-4k-console/234-7189.prd?skuId=234-7189")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Microsoft Xbox One X 1TB 4K Console",
                    "price": "449.00",
                    "url": 'https://www.tesco.com/direct/microsoft-xbox-one-x-1tb-4k-console/234-7189.prd?skuId=234-7189',
                    "inStock": true,                    
                    "stockLevel": null,
                    "image": "https://tesco.scene7.com/is/image/tesco/234-7189_PI_66650AL3?op_sharpen=1&id=sdDaH3&fmt=jpg&fit=constrain,1&wid=630&hei=506&op_sharpen=1",
                    "country": "uk",
                    "sku": '234-7189'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Pre-order Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "https://www.tesco.com/direct/a-way-out-xbox-one/542-7362.prd?skuId=542-7362",
            "html" : cache.load("https://www.tesco.com/direct/a-way-out-xbox-one/542-7362.prd?skuId=542-7362")
        }

        it('should return false when product is available for pre order', function (done) {

            Structure(payload, function(err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "A Way Out Xbox One",
                    "price": "22.00",
                    "url": payload.url,
                    "inStock": false,
                    "stockLevel": null,
                    "image": "https://tesco.scene7.com/is/image/tesco/542-7362_PI_66650AL6?op_sharpen=1&id=FBrbB0&fmt=jpg&fit=constrain,1&wid=400&hei=507&op_sharpen=1",
                    "country": "uk",
                    "sku": '542-7362'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Out of Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "https://www.tesco.com/direct/character-minions-printed-100-cotton-beach-towel/104-9626.prd?skuId=104-9626&source=others",
            "html" : cache.load("https://www.tesco.com/direct/character-minions-printed-100-cotton-beach-towel/104-9626.prd?skuId=104-9626&source=others")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Character Minions Printed 100% Cotton Beach Towel",
                    "price": "7.40",
                    "url": payload.url,
                    "inStock": false,
                    "stockLevel": null,
                    "image": "https://tesco.scene7.com/is/image/tesco/104-9626_PI_1000540MN?op_sharpen=1&id=HzNaG0&fmt=jpg&fit=constrain,1&wid=506&hei=506&op_sharpen=1",
                    "country": "uk",
                    "sku": '104-9626'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

});

 describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "https://www.toolstation.com/search?searchstr=subdept_id%3A3103&sdname=White+Low+Profile+Wiring+Accessories&depid=190&depname=Electrical&subid=3103&lgvis=y",
            "html" : cache.load("https://www.toolstation.com/search?searchstr=subdept_id%3A3103&sdname=White+Low+Profile+Wiring+Accessories&depid=190&depname=Electrical&subid=3103&lgvis=y")
        }

        // Change to be the number of products on the page
        var x = 40;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(err, result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'All Surface Swingball Set';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(err, result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '21.00',
                    url: 'http://www.tesco.com/direct/all-surface-swingball-set/215-1312.prd?skuId=215-1312',
                    image: 'https://tesco.scene7.com/is/image/tesco/215-1312_PI_TPS2933922?wid=170&hei=170&$Offers$',
                    country: 'uk',
                    sku: "215-1312"
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Giant Hopscotch';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(err, result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '7.50',
                    url: 'http://www.tesco.com/direct/giant-hopscotch/628-1485.prd?skuId=628-1485',
                    image: 'https://tesco.scene7.com/is/image/tesco/628-1485_PI_1000199MN?wid=170&hei=170&$Offers$',
                    country: 'uk',
                    sku: "628-1485",
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

         describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "https://www.tesco.com/direct/home-electrical/batteries/cat3375667.cat?catId=4294731032",
            "html" : cache.load("https://www.tesco.com/direct/home-electrical/batteries/cat3375667.cat?catId=4294731032")
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(err, pagination) {
                var expected = JSON.stringify({
                    "items": 60,
                    "pages": 1,
                    "url" : "https://www.tesco.com/direct/home-electrical/batteries/cat3375667.cat?catId=4294731032"
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    });

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "http://www.tesco.com/direct/home-electrical/electrical-accessories/cat3376649.cat?icid=homeelectrical_flyoutlink_BatteriesElectricalAccessories",
            "html" : cache.load("http://www.tesco.com/direct/home-electrical/electrical-accessories/cat3376649.cat?icid=homeelectrical_flyoutlink_BatteriesElectricalAccessories")
        }

        it('should be true when the category is correct', function (done) {

            Structure(payload, function(err, category) {
                var expected = JSON.stringify({
                    "name": "Electrical Accessories",
                    "url" : "http://www.tesco.com/direct/home-electrical/electrical-accessories/cat3376649.cat?icid=homeelectrical_flyoutlink_BatteriesElectricalAccessories"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    });


