var should = require('should');
var Structure = require('../lib/structure.js')

describe('www.toysrus.co.uk', function() {

    before(function()  {
        this.timeout(5000);
    })

    describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "http://www.toysrus.co.uk/toys/browse/age/_/N-103535?No=48&Nrpp=24"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(pagination) {
                var expected = JSON.stringify({
                    "items": 96,
                    "pages": 70
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    });

    describe('Checking to see if no pgination works', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "http://www.toysrus.co.uk/toys/browse/age/3-4-years/_/N-4294948488+103537?Nrpp=24"
        }

        it('should return 0/null items and null pages', function (done) {

            Structure(payload, function(pagination) {
                var expected = JSON.stringify({
                    "items": "",
                    "pages": null
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    });

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "http://www.toysrus.co.uk/toys/browse/age/_/N-103535?No=48&Nrpp=24"
        }

        it('should be true when the category is correct', function (done) {

            Structure(payload, function(category) {
                var expected = JSON.stringify({
                    "name": "Age"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "http://www.toysrus.co.uk/toys/browse/saving-events/save-up-to-33-off-lego-ninjago/_/N-1949705443?ab=tru-lego:hero1:33-off-lego-ninjago"
        }

        it('should return 24 products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(24);
                done()
            })
        });

        it('last product should be:', function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: 'LEGO Ninjago Attack of Morro Dragon (70736)',
                    price: '40.18',
                    url: 'http://www.toysrus.co.uk/toys/lego-ninjago-attack-of-morro-dragon-70736-/0150691',
                    image: 'http://truimg.toysrus.co.uk/product/images/UK/0150691_CF0001.jpg?resize=150:150',
                    country: 'uk',
                    sku: "0150691"
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

        it('first product should be:', function(done) {

            Structure(payload, function(result) {

                 var first = result.shift()
                    var expected = JSON.stringify({
                        barcode: null,
                        name: 'LEGO Ninjago Dawn of Iron Doom (70626)',
                        price: '40.18',
                        url: 'http://www.toysrus.co.uk/toys/lego-ninjago-dawn-of-iron-doom-70626-/TRUP2178910001',
                        image: 'http://truimg.toysrus.co.uk/product/images/UK/TRUP2178910001_CF0001.jpg?resize=150:150',
                        country: 'uk',
                        sku: "TRUP2178910001"
                    })

                    JSON.stringify(first).should.equal(expected)
                    done()
                })

        })

    });


    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.toysrus.co.uk/toys/lego-dimensions-dc-superman-fun-pack-/TRUP2374720001"
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "LEGO Dimensions DC Superman Fun Pack",
                    "price": "14.99",
                    "url": "http://www.toysrus.co.uk/toys/lego-dimensions-dc-superman-fun-pack-/TRUP2374720001",
                    "inStock": true,
                    "stockLevel": null,
                    "image": "http://www.toysrus.co.uk/products/images/large/TRUP2374720001_CF0001.jpg",
                    "country": "uk",
                    "sku" : "TRUP2374720001"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Out of Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.toysrus.co.uk/toys/schleich-blue-whale-figure/0107398"
        }

        it('should return false when product is out of stock', function(done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "Schleich Blue Whale Figure",
                    "price": "5.98",
                    "url": 'http://www.toysrus.co.uk/toys/schleich-blue-whale-figure/0107398',
                    "inStock": false,
                    "stockLevel": null,
                    image: 'http://truimg.toysrus.co.uk/product/images/UK/0107398_CF0001.jpg?resize=500:500',
                    "country": "uk",
                    "sku" : "03552401"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        })

    })

    // describe('Stock Removed Page', function() {

    //     var payload = {
    //         "pageType" : "product",
    //         "url" : "http://www.toysrus.co.uk/toys/thumpin-thumper/0110553"
    //     }

    //     it('should return false when product is out of stock', function(done) {

    //         Structure(payload, function(inStockPage) {
    //             var expected = JSON.stringify({
    //                 "name": "",
    //                 "price": "",
    //                 "url": null,
    //                 "inStock": false,
    //                 "stockLevel": null
    //             })
    //             JSON.stringify(inStockPage).should.equal(expected);
    //             done()
    //         })
    //     })

    // })

});