var should = require('should');
var Structure = require('../lib/structure.js')

describe('replaceWithwebAddress', function() {

    this.timeout(10000);

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.truevalue.com/product/Pet-Supplies/Dog-Supplies/Dog-Toys/Crunchable-Dog-Ball-2-5-In-/pc/14/c/2782/sc/2792/71188.uts"
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "Nerf Crunchable Dog Ball, 2.5-In.",
                    "price": "6.99",
                    "url": null,
                    "inStock": true,
                    "stockLevel": null,
                    "image": 'http://www.truevalue.com/assets/product_images/styles/xlarge/153911.jpg',
                    "country": 'us',
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    // describe('Out of Stock Page', function() {

    //     var payload = {
    //         "pageType" : "product",
    //         "url" : ""
    //     }

    //     it('should be false when product is in store only', function (done) {

    //         Structure(payload, function(inStockPage) {
    //             var expected = JSON.stringify({
    //                 "name": "",
    //                 "price": "",
    //                 "url": null,
    //                 "inStock": false,
    //                 "stockLevel": null
    //             })
    //             JSON.stringify(inStockPage).should.equal(expected);
    //             done()
    //         })
    //     });

    // });

    describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "http://www.truevalue.com/thumbnail/Pet-Supplies/Dog-Supplies/Dog-Toys/pc/14/c/2782/2792.uts"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(pagination) {
                var expected = JSON.stringify({
                    "items": 16,
                    "pages": 6
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    });

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "http://www.truevalue.com/thumbnail/Pet-Supplies/Dog-Supplies/Dog-Toys/pc/14/c/2782/2792.uts"
        }

        it('should be true when the category is correct', function (done) {

            Structure(payload, function(category) {
                var expected = JSON.stringify({
                    "name": "Dog Toys"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "http://www.truevalue.com/thumbnail/Pet-Supplies/Dog-Supplies/Dog-Toys/pc/14/c/2782/2792.uts"
        }

        // Change to be the number of products on the page
        var x = 16;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Nerf Crunchable Dog Ball, 2.5-In.';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '6.99',
                    url: 'www.truevalue.com/product/Pet-Supplies/Dog-Supplies/Dog-Toys/Crunchable-Dog-Ball-2-5-In-/pc/14/c/2782/sc/2792/71188.uts',
                    image: 'http://www.truevalue.com/assets/product_images/styles/medium/153911.jpg',
                    country: 'us'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Petstages Dog Toy, Petite Orka Bone, Pr.';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '7.99',
                    url: 'www.truevalue.com/product/Pet-Supplies/Dog-Supplies/Dog-Toys/Dog-Toy-Petite-Orka-Bone-Pr-/pc/14/c/2782/sc/2792/68923.uts',
                    image: 'http://www.truevalue.com/assets/product_images/styles/medium/164020.jpg',
                    country: 'us'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});
