    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "http://www.tvstoybox.com/0-12-months"
        }

        // Change to be the number of products on the page
        var x = 36;
        it('should return ' + x + ' number of products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Barney Singing I Love You Barney 10" Plush';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '7.90',
                    url: 'http://www.tvstoybox.com/barney-singing-i-love-you-barney-10-plush',
                    image: 'http://www.tvstoybox.com/media/catalog/product/cache/1/small_image/170x/9df78eab33525d08d6e5fb8d27136e95/BAR-1040-49978.jpg',
                    country: 'us',
                    sku: 'barney-singing-i-love-you-barney-10-plush'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Sesame Street Cookie Monster 12x12 Canvas Wall Art';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '29.99',
                    url: 'http://www.tvstoybox.com/sesame-street-cookie-monster-12x12-canvas-wall-art',
                    image: 'http://www.tvstoybox.com/media/catalog/product/cache/1/small_image/170x/9df78eab33525d08d6e5fb8d27136e95/1/7/170_58334.jpg',
                    country: 'us',
                    sku: 'sesame-street-cookie-monster-12x12-canvas-wall-art'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

     describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "http://www.tvstoybox.com/0-12-months"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(pagination) {
                var expected = JSON.stringify({
                    "items": 36,
                    "pages": 7
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    });

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "http://www.tvstoybox.com/0-12-months"
        }

        it('should be true when the category is correct', function (done) {

            Structure(payload, function(category) {
                var expected = JSON.stringify({
                    "name": "0-12 Months"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });


