var should = require('should');
var Structure = require('../lib/structure.js')


describe('www.upcitemdb.com/', function() {

    before(function()  {
        this.timeout(5000); 
    })


    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "http://www.upcitemdb.com/query?s=skyrim&type=2"
        }

        it('should return all 75 barcodes', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(75);
                done()
            })
        });

        it('last barcode should be 765857291741', function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: '765857291741',
                    name: 'The Elder Scrolls Skyrim Dragonborn Metal Dog Tag GE1219',
                    price: null,
                    url: 'http://www.upcitemdb.com/upc/765857291741',
                    country: ''

                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

        it('first barcode should be 093155147775', function(done) {
            Structure(payload, function(result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: '093155147775',
                    name: 'The Elder Scrolls V Skyrim - Legendary Edition - PC Game',
                    price: null,
                    url: 'http://www.upcitemdb.com/upc/93155147775',
                    country: ''
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

    });

});
