var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')

describe('www.vitabiotics.com', function () {

    this.timeout(10000);


    describe('In stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "https://www.vitabiotics.com/shop/category/energy/ultra/green-tea",
            "html": cache.load("https://www.vitabiotics.com/shop/category/energy/ultra/green-tea")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Ultra Green Tea",
                    "price": "5.95",
                    "url": payload.url,
                    "inStock": true,
                    "stockLevel": null,
                    "image": "https://vitabiotics.azureedge.net/uk/content/img/packshots/img_466.png",
                    "country": "uk"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Out of Stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "https://www.vitabiotics.com/shop/hairfollic/man",
            "html": cache.load("https://www.vitabiotics.com/shop/hairfollic/man")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Hairfollic Man",
                    "price": "18.95",
                    "url": payload.url,
                    "inStock": false,
                    "stockLevel": null,
                    "image": "https://vitabiotics.azureedge.net/uk/content/img/packshots/img_456.png",
                    "country": "uk"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

});

describe('Search Page', function () {

    var payload = {
        "pageType": "search",
        "url": "https://www.vitabiotics.com/shop/category/energy",
        "html": cache.load("https://www.vitabiotics.com/shop/category/energy")
    }

    // Change to be the number of products on the page
    var x = 18;
    it('should return ' + x + ' number of products', function (done) {
        Structure(payload, function (err, result) {
            result.length.should.equal(x);
            done()
        })
    });

    var firstProductName = 'Feroglobin Liquid';
    it('first product should be ' + firstProductName, function (done) {
        Structure(payload, function (err, result) {
            var first = result.shift()
            var expected = JSON.stringify({
                barcode: null,
                name: firstProductName,
                price: '5.85',
                url: 'https://www.vitabiotics.com/shop/category/energy/feroglobin/liquid',
                image: 'https://vitabiotics.azureedge.net/uk/content/img/packshots/img_54.png',
                country: 'uk'
            })
            JSON.stringify(first).should.equal(expected)
            done()
        })

    })

    var lastProductName = 'Omega-H3 Liquid';
    it('last product should be ' + lastProductName, function (done) {
        Structure(payload, function (err, result) {
            var last = result.pop()
            var expected = JSON.stringify({
                barcode: null,
                name: lastProductName,
                price: '8.45',
                url: 'https://www.vitabiotics.com/shop/category/energy/omega-h3/liquid',
                image: 'https://vitabiotics.azureedge.net/uk/content/img/packshots/img_523.png',
                country: 'uk'
            })

            JSON.stringify(last).should.equal(expected)
            done()
        });
    })

    describe('Search page category', function () {

        var payload = {
            "pageType": "category",
            "url": "https://www.vitabiotics.com/shop/category/energy",
            "html": cache.load("https://www.vitabiotics.com/shop/category/energy")
        }

        it('should be true when the category is correct', function (done) {

            Structure(payload, function (err, category) {
                var expected = JSON.stringify({
                    "name": "Energy",
                    "url": payload.url 
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

});
