    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "http://www.vitaminshoppe.com/b/dymatize-nutrition-protein-blends/xfilters/N-92tZ8efZcp9531Z1z12iha"
        }

        // Change to be the number of products on the page
        var x = 3;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Super Mass Gainer - RICH CHOCOLATE (12 Pound Powder)';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '59.99',
                    url: 'http://www.vitaminshoppe.com/p/super-mass-gainer-rich-chocolate-12-lb-powder/d4-1145',
                    image: 'http://s7.vitaminshoppe.com/is/image/VitaminShoppe/2072643_set?$PLPSKU$',
                    country: 'us'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Super Mass Gainer - COOKIES & CREAM (12 Pound Powder)';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '59.99',
                    url: 'http://www.vitaminshoppe.com/p/super-mass-gainer-cookies-cream-12-lb-powder/d4-1146',
                    image: 'http://s7.vitaminshoppe.com/is/image/VitaminShoppe/2072668_set?$PLPSKU$',
                    country: 'us'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

