var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')

describe('replaceWithwebAddress', function() {

    this.timeout(10000);

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "https://www.vminnovations.com/Product_54213/Hamilton-Beach-300W-6-Speed-Stand-Mixer-63326-.html",
            "html": cache.load("https://www.vminnovations.com/Product_54213/Hamilton-Beach-300W-6-Speed-Stand-Mixer-63326-.html")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Hamilton Beach 300W 6-Speed Stand Mixer | 63326",
                    "price": "89.99",
                    "url": payload.url,
                    "inStock": true,
                    "stockLevel": null,
                    "image": "https://www.vminnovations.com/sys/resource.ashx?guid=32d977108b584006994a5491da9f283f&w=750&h=500&p=10",
                    "country": "us"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    // describe('Out of Stock Page', function() {

    //     var payload = {
    //         "pageType" : "product",
    //         "url" : ""
    //     }

    //     it('should be false when product is in store only', function (done) {

    //         Structure(payload, function(inStockPage) {
    //             var expected = JSON.stringify({
    //                 "name": "",
    //                 "price": "",
    //                 "url": null,
    //                 "inStock": false,
    //                 "stockLevel": null
    //             })
    //             JSON.stringify(inStockPage).should.equal(expected);
    //             done()
    //         })
    //     });

    // });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "https://www.vminnovations.com/Browse_2654/Accessories.html",
            "html": cache.load("https://www.vminnovations.com/Browse_2654/Accessories.html")
        }

        // Change to be the number of products on the page
        var x = 16;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(err, result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Hamilton Beach 6-Speed Hand Mixer';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(err, result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '24.99',
                    url: 'http://www.vminnovations.com/Product_82341/Hamilton-Beach-6-Speed-Hand-Mixer.html',
                    image: 'https://srv2.vm-images.net/res/s_258/881fae8576f04088922484c6c7be96a0.jpg',
                    country: 'us'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Sunbeam Stand Mixer, White';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(err, result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '96.99',
                    url: 'http://www.vminnovations.com/Product_79773/Sunbeam-Stand-Mixer-White.html',
                    image: 'https://srv3.vm-images.net/res/s_258/aa2dcbdc627b43f9b5e0a72c72f1a11e.jpg',
                    country: 'us'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

    describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "https://www.vminnovations.com/Browse_2214/Swimming-Pools.html",
            "html": cache.load("https://www.vminnovations.com/Browse_2214/Swimming-Pools.html")
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(err, pagination) {
                var expected = JSON.stringify({
                    "items": 40,
                    "pages": 3,
                    "url" : "https://www.vminnovations.com/Browse_2214/Swimming-Pools.html"
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    });

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "https://www.vminnovations.com/Browse_2033/Dog-and-Cat-Supplies.html",
            "html": cache.load("https://www.vminnovations.com/Browse_2033/Dog-and-Cat-Supplies.html")
        }

        it('should be true when the category is correct', function (done) {

            Structure(payload, function(err, category) {
                var expected = JSON.stringify({
                    "name": "Dog & Cat Supplies",
                    "url" : "https://www.vminnovations.com/Browse_2033/Dog-and-Cat-Supplies.html"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

});
