var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')


describe('replaceWithwebAddress', function () {

    this.timeout(10000);

    describe('In Stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "https://www.vtech.co.uk/product/detail/15837/InnoTab_Max_Pink",
            "html": cache.load("https://www.vtech.co.uk/product/detail/15837/InnoTab_Max_Pink")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "InnoTab Max Pink",
                    "price": "99.99",
                    "url": payload.url,
                    "inStock": "UNCHECKED",
                    "stockLevel": null,
                    "image": 'https://www.vtech.co.uk/assets/data/products/{4AEBF70C-695E-4768-A970-740FC7B09910}/images/166853InnoTabMaxpinkwithpen_large.jpg',
                    "country": 'uk',
                    "description": "Innotab Max is the next generation in children's interactive play! An even faster console features a 7 inch multi-touch screen, 2MP camera for photo and video taking, Kid Connect Premium app, wi-fi, kid-safe internet browser, Movie Maker app, media player, e-books and lots more. Connect to the VTech Learning Lodge to download videos, music, apps, e-books, games and more! There is 4GB of internal memory that is expandable with a microSD card (not included). Built-in rechargeable battery for portable play!",
                    "sku":"15837"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    /*describe('Out of Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : ""
        }

        it('should be false when product is in store only', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "",
                    "price": "",
                    "url": null,
                    "inStock": false,
                    "stockLevel": null,
                    "image": '',
                    "country": '',
                    "description": ''
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });*/

    describe('Search page pagination', function () {

        var payload = {
            "pageType": "pagination",
            "url": "https://www.vtech.co.uk/product/search/_/inno",
            "html": cache.load("https://www.vtech.co.uk/product/search/_/inno")
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function (err, pagination) {
                var expected = JSON.stringify({
                    "items": 20,
                    "pages": 3,
                    "url": "https://www.vtech.co.uk/product/search/_/inno"
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    })

    describe('Search page category', function () {

        var payload = {
            "pageType": "category",
            "url": "https://www.vtech.co.uk/product/search/_/inno",
            "html": cache.load("https://www.vtech.co.uk/product/search/_/inno")
        }

        it('should be true when the category is correct', function (done) {

            Structure(payload, function (err, category) {
                var expected = JSON.stringify({
                    "name": "inno",
                    "url": "https://www.vtech.co.uk/product/search/_/inno"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    describe('Search Page', function () {

        var payload = {
            "pageType": "search",
            "url": "https://www.vtech.co.uk/product/plus/ages/0/true",
            "html": cache.load("https://www.vtech.co.uk/product/plus/ages/0/true")
        }

        // Change to be the number of products on the page
        var x = 20;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function (err, result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Pull & Pop Aeroplane';
        it('first product should be ' + firstProductName, function (done) {
            Structure(payload, function (err, result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '15.99',
                    url: 'https://www.vtech.co.uk/product/detail/18443',
                    image: 'https://www.vtech.co.uk/assets/data/products/{8726CF8E-C330-4F41-AA61-CF1FEDB30C98}/images/508803topproduct_thumb_detail.jpg',
                    country: 'uk',
                    sku: '18443'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Toot-Toot Drivers Baby Driver';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function (err, result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '17.99',
                    url: 'https://www.vtech.co.uk/product/detail/17847',
                    image: 'https://www.vtech.co.uk/assets/data/products/{FC3ECBAF-78ED-4DE8-9F77-312196D08B28}/images/_72A8765-FINAL-low-res_thumb_detail.jpg',
                    country: 'uk',
                    sku:"17847"
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});
