var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')

describe('replaceWithDomainName', function () {

    this.timeout(5000);

    describe('In Stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "https://www.waitrosegarden.com/product/_/felco-original-secateurs-model-no-2/classid.200601/",
            "html": cache.load("https://www.waitrosegarden.com/product/_/felco-original-secateurs-model-no-2/classid.200601/")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Felco original secateurs (model no 2)",
                    "price": "53.99",
                    "url": payload.url,
                    "inStock": true,
                    "stockLevel": null,
                    "image": "https://img.crocdn.co.uk/images/products2/pr/00/00/20/06/pr0000200601_card4_lg.jpg",
                    "country": 'uk'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    xdescribe('Out of Stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "",
            "html": cache.load("")
        }

        it('should be false when product is in store only', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "",
                    "price": "",
                    "url": payload.url,
                    "inStock": false,
                    "stockLevel": null,
                    "image": '',
                    "country": '',
                    "description": ''
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    xdescribe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "",
            "html": cache.load("")
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(err, category) {
                var expected = JSON.stringify({
                    "name": ""
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    xdescribe('Search page pagination', function () {

                var payload = {
                    "pageType": "pagination",
                    "url": "",
                    "html": cache.load("")
                }

                it('should be true when number of items and pages correct', function (done) {

                    Structure(payload, function (err, pagination) {
                        var expected = JSON.stringify({
                            "items": '',
                            "pages": ''
                        })
                        JSON.stringify(pagination).should.equal(expected);
                        done()
                    })
                });

            });


    describe('Search Page', function () {

        var payload = {
            "pageType": "search",
            "url": "https://www.waitrosegarden.com/products/_/the-tool-shed/garden-tools/plcid.767/plcid.778/",
            "html": cache.load("https://www.waitrosegarden.com/products/_/the-tool-shed/garden-tools/plcid.767/plcid.778/")
        }

        // Change to be the number of products on the page
        var x = 20;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function (err, result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Felco original secateurs (model no 2)';
        it('first product should be ' + firstProductName, function (done) {
            Structure(payload, function (err, result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '53.99',
                    url: 'https://www.waitrosegarden.com/product/_/felco-original-secateurs-model-no-2/classid.200601/',
                    image: 'https://img.crocdn.co.uk/images/products2/pr/00/00/20/06/pr0000200601.jpg?width=440&height=440',
                    country: 'uk'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'De Wit hand fork';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function (err, result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '22.99',
                    url: 'https://www.waitrosegarden.com/product/_/de-wit-hand-fork/classid.2000009221/',
                    image: 'https://img.crocdn.co.uk/images/products2/pr/20/00/00/92/pr2000009221.jpg?width=440&height=440',
                    country: 'uk'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});


