var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')

describe('replaceWithwebAddress', function() {

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "https://www.watches2u.com/watches/bulova/96a175-mens-silver-tone-chronograph-watch.html",
            "html": cache.load("https://www.watches2u.com/watches/bulova/96a175-mens-silver-tone-chronograph-watch.html")

        }

        it('should be true when product is in stock', function () {
            
            const inStockPage =  Structure(payload)            
            inStockPage.name.should.be.equal("Money Maze Bank");
            inStockPage.price.should.be.equal("14.99");
            inStockPage.url.should.be.equal("https://www.bitsandpieces.com/product/money-maze-bank");
            inStockPage.inStock.should.be.equal(true);
            should(inStockPage.stockLevel).be.equal(null);
            inStockPage.inStock.should.be.equal(true);
            inStockPage.image.should.be.equal('https://s3.amazonaws.com/cdn.bitsandpieces.com/images/395/42768.jpg');
            inStockPage.country.should.be.equal('us');            
         });
 
 
    });

    describe('Out of Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "https://www.watches2u.com/watches/casio/eqb-600d-1a2er-mens-edifice-solar-powered-silver-steel-bracelet-watch.html"
        }

        it('should be false when product is in store only', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "Casio Mens Edifice Solar Powered Silver Steel Bracelet Watch EQB-600D-1A2ER",
                    "price": null,
                    "url": null,
                    "inStock": false,
                    "stockLevel": null,
                    "image": 'https://www.watches2u.com/watches/casio/eqb-600d-1a2er-mens-edifice-solar-powered-silver-steel-bracelet-watch.jpg',
                    "country": 'uk',
                    "description": 'Casio mens Watch EQB-600D-1A2ER.Presenting this trendy analogue mens watch which has been precision built by Casio. This piece includes a silver stainless steel bracelet with a stainless steel case together with a black face. Our EQB-600D-1A2ER Casio mens wrist watch comes with the 2 years manufacturer certificate of warranty. Casio suggest that you purchase your EQB-600D-1A2ER Casio mens wrist watch from an official agent such as Watches2U. Casio EQB-600D-1A2ER Reviews. There are 1 customer reviews written for the Casio mens EQB-600D-1A2ER wristwatch. The EQB-600D-1A2ER Casio mens wristwatch is one of many watches available in our great collection of Casio watches.'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "https://www.watches2u.com/find.do?per_page=288&search_string=silver"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(pagination) {
                var expected = JSON.stringify({
                    "items": 288,
                    "pages": 7
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    })

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "https://www.watches2u.com/find.do?per_page=288&search_string=silver"
        }

        it('should be true when the category is correct', function (done) {

            Structure(payload, function(category) {
                var expected = JSON.stringify({
                    "name": "Search Results"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "https://www.watches2u.com/find.do?per_page=288&search_string=silver",
            "html": cache.load("https://www.watches2u.com/find.do?per_page=288&search_string=silver")
        }

        // Change to be the number of products on the page
        var x = 7;
        it('should return ' + x + ' numer of products', function () {
            let result = Structure(payload)
            result.length.should.equal(x);
        });

        var firstProductName = 'Critters Go For A Ride 100 Large Piece Jigsaw Puzzle';
        it('first product should be ' + firstProductName, function() {

            var expected = JSON.stringify({
                barcode: null,
                name: firstProductName,
                price: '4.99',
                url: 'http://www.bitsandpieces.com/product/critters-go-for-a-ride-100-large-piece-jigsaw-puzzle-42777',
                image: 'https://s3.amazonaws.com/cdn.bitsandpieces.com/images/160/42777.jpg',
                country: 'us'
            })
            
            let result = Structure(payload)

            var first = result.shift()
            
            JSON.stringify(first).should.equal(expected)

        })

        var lastProductName = 'Soccer Sliding Wooden Puzzle';
        it('last product should be ' + lastProductName, function () {
            var expected = JSON.stringify({
                barcode: null,
                name: lastProductName,
                price: '14.99',
                url: 'http://www.bitsandpieces.com/product/soccer-sliding-wooden-puzzle',
                image: 'https://s3.amazonaws.com/cdn.bitsandpieces.com/images/160/43952.jpg',
                country: 'us'
            })

            const result = Structure(payload);

            var last = result.pop()
            JSON.stringify(last).should.equal(expected)
        })

    }); 

});
