var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')

describe('replaceWithwebAddress', function() {

    this.timeout(10000);

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "https://www.wexphotovideo.com/celestron-x-cel-lx-18mm-eyepiece-1533104/",
            "html" : cache.load("https://www.wexphotovideo.com/celestron-x-cel-lx-18mm-eyepiece-1533104/")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Celestron X-Cel LX 18mm Eyepiece",
                    "price": "79.00",
                    "url": payload.url,
                    "inStock": true,
                    "stockLevel": null,
                    "image": 'https://www.wexphotovideo.com/globalassets/product-images/1533000/1533104.jpg',
                    "country": 'uk',
                    "description": "The newly enhanced X-Cel LX eyepiece series is what you've been waiting for in a high quality eyepiece for planetary viewing. With a brand new sleek, robust design, and a twist-up eye guard, these eyepieces are especially designed for comfort and ease of use. They also have a wide 60 Degree field of view and 6-element fully multi-coated optics for magnificent wide-field performance. X-Cel LX eyepieces are Parfocal and require little to no focusing when changing from low to high power. Available in focal lengths of; 2.3mm, 5mm, 7mm, 9mm, 12mm, 18mm and 25mm  More"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    // describe('Out of Stock Page', function() {

    //     var payload = {
    //         "pageType" : "product",
    //         "url" : ""
    //     }

    //     it('should be false when product is in store only', function (done) {

    //         Structure(payload, function(inStockPage) {
    //             var expected = JSON.stringify({
    //                 "name": "",
    //                 "price": "",
    //                 "url": null,
    //                 "inStock": false,
    //                 "stockLevel": null,
    //                 "image": '',
    //                 "country": '',
    //                 "description": ''
    //             })
    //             JSON.stringify(inStockPage).should.equal(expected);
    //             done()
    //         })
    //     });

    // });

    describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "https://www.wexphotovideo.com/eyepiece-sets/?pagenum=3",
            "html" : cache.load("https://www.wexphotovideo.com/eyepiece-sets/?pagenum=3")
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(err, pagination) {
                var expected = JSON.stringify({
                    "items": 10,
                    "pages": 11
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    })

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "https://www.wexphotovideo.com/eyepiece-sets/?pagenum=3",
            "html" : cache.load("https://www.wexphotovideo.com/eyepiece-sets/?pagenum=3")
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(err, category) {
                var expected = JSON.stringify({
                    "name": "Eyepiece Sets | Astronomy | Wex Photo Video",
                    "url" : "https://www.wexphotovideo.com/eyepiece-sets/?pagenum=3"

                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "https://www.wexphotovideo.com/canon-digital-slr-cameras/",
            "html" : cache.load("https://www.wexphotovideo.com/canon-digital-slr-cameras/")
        }

        // Change to be the number of products on the page
        var x = 12;
        it('should return ' + x + ' number of products', function (done) {
            Structure(payload, function(err, result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Canon EOS 5D Mark IV Digital SLR Camera Body';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(err, result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '3249.00',
                    url: 'http://www.wexphotoandvideo.com/canon-eos-5d-mark-iv-digital-slr-camera-body-1605233/',
                    image: 'http://www.wexphotoandvideo.com/globalassets/product-images/1605000/1605233.jpg?width=100',
                    country: 'uk',
                    sku: '1605233'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Canon EOS 1300D Digital SLR Camera with 18-55mm IS II Lens';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(err, result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '369.00',
                    url: 'http://www.wexphotoandvideo.com/canon-eos-1300d-digital-slr-camera-with-18-55mm-is-ii-lens-1594128/',
                    image: 'http://www.wexphotoandvideo.com/globalassets/product-images/1594000/1594128.jpg?width=100',
                    country: 'uk',
                    sku: '1594128'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});
