var should = require('should');
var Structure = require('../lib/structure.js')

describe('www.wickes.co.uk', function() {

    this.timeout(10000);

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.wickes.co.uk/Irwin-880-Plus-Universal-Handsaw-20in/p/112802"
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "Irwin 880 Plus Universal Handsaw 20in",
                    "price": "7.19",
                    "url": null,
                    "inStock": true,
                    "stockLevel": null,
                    "image": 'http://travisperkins.scene7.com/is/image/travisperkins/I0534_112802_00?id=Hx2Sr2&fmt=jpg&fit=constrain,1&wid=310&hei=91',
                    "country": 'uk'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Out of Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.wickes.co.uk/Irwin-Jack-JAK770UHP550-Soft-Grip-Handsaw-22in/p/226386"
        }

        it('should be false when product is in store only', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "Irwin Jack JAK770UHP550 Soft Grip Handsaw 22in",
                    "price": null,
                    "url": null,
                    "inStock": false,
                    "stockLevel": null,
                    "image": 'http://travisperkins.scene7.com/is/image/travisperkins/I0534_226386_00?id=vnxTB0&fmt=jpg&fit=constrain,1&wid=310&hei=310',
                    "country": 'uk'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "http://www.wickes.co.uk/Products/Tools+Workwear/Hand-Tools/Hand-Saws+Blades/c/1000402"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(pagination) {
                var expected = JSON.stringify({
                    "items": 120,
                    "pages": 0
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    })

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "http://www.wickes.co.uk/Products/Tools+Workwear/Hand-Tools/Hand-Saws+Blades/c/1000402"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(category) {
                var expected = JSON.stringify({
                    "name": "Hand Saws & Blades"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });


    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "http://www.wickes.co.uk/Products/Tools+Workwear/Hand-Tools/Hand-Saws+Blades/c/1000402"
        }

        // Change to be the number of products on the page
        var x = 15;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Irwin 880 Plus Universal Handsaw 20in';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '7.19',
                    url: 'http://www.wickes.co.uk/Irwin-880-Plus-Universal-Handsaw-20in/p/112802',
                    image: 'http://travisperkins.scene7.com/is/image/travisperkins/I0534_112802_00?$normal$',
                    country: 'uk'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Wickes Mini Hacksaw with Blade 6in';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '5.99',
                    url: 'http://www.wickes.co.uk/Wickes-Mini-Hacksaw-with-Blade-6in/p/186525',
                    image: 'http://travisperkins.scene7.com/is/image/travisperkins/D9406_186525_00?$normal$',
                    country: 'uk'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});
