var should = require('should');
var Structure = require('../lib/structure.js')

describe('replaceWithwebAddress', function() {

    this.timeout(10000);

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.wiggle.co.uk/charge-cooker-0-2016/"
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "Charge Cooker 0 (2016)",
                    "price": "489.99",
                    "url": null,
                    "inStock": true,
                    "stockLevel": null,
                    "image": 'http://www.wigglestatic.com/product-media/5360108422/Charge-Cooker-0-2016-Hard-Tail-MTBs-Grey-BYCHM6COOKSSMGRY.jpg?w=430&h=430&a=7',
                    "country": 'uk',
                    "description": 'The 2016 Charge Cooker 0 with its refined geometry and 27Plus wheels gives the speed and performance of a 29er but with the technical handling of a traditional 650b. The perfect all-rounder mountain bike.'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Out of Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.wiggle.co.uk/sram-x1-11-speed-chain/"
        }

        it('should be false when product is in store only', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "SRAM X1 11 Speed Chain",
                    "price": "18.94",
                    "url": null,
                    "inStock": false,
                    "stockLevel": null,
                    "image": 'http://www.wigglestatic.com/product-media/5360099669/sram-x1-chain.jpg?w=430&h=430&a=7',
                    "country": 'uk',
                    "description": "The SRAM X1 11 Speed X-Sync Chain continues SRAM's line of durable, dependable, light weight chains. Add SRAM's XX1 geometry and you can be confident of smooth consistent shifting on every ride."
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "http://www.wiggle.co.uk/bikes/?ps=96"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(pagination) {
                var expected = JSON.stringify({
                    "items": 96,
                    "pages": 5
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    })

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "http://www.wiggle.co.uk/bikes/?ps=96"
        }

        it('should be true when the category is correct', function (done) {

            Structure(payload, function(category) {
                var expected = JSON.stringify({
                    "name": "Bikes"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "http://www.wiggle.co.uk/bikes/?ps=96"
        }

        // Change to be the number of products on the page
        var x = 93;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'GT Helion Elite (2016)';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '1019.99',
                    url: 'http://www.wiggle.co.uk/gt-helion-elite-2016/',
                    image: 'http://www.wigglestatic.com/product-media/5360108541/GT-Helion-Elite-2016-Full-Suspension-MTBs-Blue-BYGTM6HELELSMBLU-0.jpg?w=200&h=200&a=7',
                    country: 'uk'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Chappelli Vintage Single Speed (2016)';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '327.25',
                    url: 'http://www.wiggle.co.uk/chappelli-vintage-single-speed-2016/',
                    image: 'http://www.wigglestatic.com/product-media/5360113380/Chappelli-Vintage-Single-Speed-2016-Single-Speed-Bikes-Silver-Green-VSS_JEN_50-2.jpg?w=200&h=200&a=7',
                    country: 'uk'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});
