    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "https://www.wilsonsleather.com/category/handbags-accessories/all.do?c=100193.100238&Brand=Andrew+Marc&pp=27&sortby=ourPicksAscend&cx=0"
        }

        // Change to be the number of products on the page
        var x = 7;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Marc New York Faux-Leather Croco Crossbody';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '39.99',
                    url: 'https://www.wilsonsleather.com/product/marc-new-york-faux-leather-croco-crossbody.do?sortby=ourPicksAscend&from=fn',
                    image: 'https://ii.wilsonsleather.com/fcgi-bin/iipsrv.fcgi?FIF=/images/wilsonsleather/source/54883_54883c01.tif&wid=220&cvt=jpeg',
                    country: 'us'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Marc New York Mini Winged Faux-Leather Satchel w/Studs';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '69.99',
                    url: 'https://www.wilsonsleather.com/product/marc-new-york-mini-winged-faux-leather-satchel-w-studs.do?sortby=ourPicksAscend&from=fn',
                    image: 'https://ii.wilsonsleather.com/fcgi-bin/iipsrv.fcgi?FIF=/images/wilsonsleather/source/M-W8481_mw8481c98e.tif&wid=220&cvt=jpeg',
                    country: 'us'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

