var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')

describe('www.winfieldoutdoors.co.uk', function () {

    this.timeout(10000);


    describe('In stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "https://www.winfieldsoutdoors.co.uk/jack-wolfskin-womens-helsinki-waterproof-jacket/",
            "html": cache.load("https://www.winfieldsoutdoors.co.uk/jack-wolfskin-womens-helsinki-waterproof-jacket/")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Jack Wolfskin Womens Helsinki Waterproof Jacket",
                    "price": "29.99",
                    "url": payload.url,
                    "inStock": true,
                    "stockLevel": null,
                    "image": "https://cdn.winfieldsoutdoors.co.uk/media/catalog/product/cache/1/image/1800x/040ec09b1e35df139433887a97daa66f/1/1/1110471-greenish-grey-jack-wolfskin-womens-helsinki-jacket-1.jpg",
                    "country": "uk"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Out of Stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "https://www.winfieldsoutdoors.co.uk/weird-fish-men-s-harrisburg-soft-knit-hoodie/",
            "html": cache.load("https://www.winfieldsoutdoors.co.uk/weird-fish-men-s-harrisburg-soft-knit-hoodie/")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Weird Fish Men's Harrisburg Soft Knit Hoodie",
                    "price": "39.99",
                    "url": payload.url,
                    "inStock": false,
                    "stockLevel": null,
                    "image": "https://cdn.winfieldsoutdoors.co.uk/media/catalog/product/cache/1/image/1000x1000/9df78eab33525d08d6e5fb8d27136e95/1/0/10236-476-wierdfish-mens-harrisburg-fleece-hoodie-2.1530151223.jpg",
                    "country": "uk"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

});

describe('Search Page', function () {

    var payload = {
        "pageType": "search",
        "url": "https://www.winfieldsoutdoors.co.uk/women/clothing/jackets-gilets/",
        "html": cache.load("https://www.winfieldsoutdoors.co.uk/women/clothing/jackets-gilets/")
    }

    // Change to be the number of products on the page
    var x = 24;
    it('should return ' + x + ' number of products', function (done) {
        Structure(payload, function (err, result) {
            result.length.should.equal(x);
            done()
        })
    });

    var firstProductName = "Berghaus Women's Stormcloud Waterproof Jacket";
    it('first product should be ' + firstProductName, function (done) {
        Structure(payload, function (err, result) {
            var first = result.shift()
            var expected = JSON.stringify({
                barcode: null,
                name: firstProductName,
                price: '84.99',
                url: 'https://www.winfieldsoutdoors.co.uk/berghaus-women-s-stormcloud-waterproof-jacket/',
                image: 'https://cdn.winfieldsoutdoors.co.uk/media/catalog/product/cache/1/small_image/375x/9df78eab33525d08d6e5fb8d27136e95/4/-/4-21199-z62-berghaus-womens-stormcloud-jacket-1.1530104027.jpg',
                country: 'uk'
            })
            JSON.stringify(first).should.equal(expected)
            done()
        })

    })

    var lastProductName = 'Regatta Womens Ballencia Bodywarmer';
    it('last product should be ' + lastProductName, function (done) {
        Structure(payload, function (err, result) {
            var last = result.pop()
            var expected = JSON.stringify({
                barcode: null,
                name: lastProductName,
                price: '19.99',
                url: 'https://www.winfieldsoutdoors.co.uk/regatta-womens-ballencia-bodywarmer/',
                image: 'https://cdn.winfieldsoutdoors.co.uk/media/catalog/product/cache/1/small_image/375x/9df78eab33525d08d6e5fb8d27136e95/r/w/rwb081-808-regatta-womens-ballencia-1.1536804201.jpg',
                country: 'uk'
            })

            JSON.stringify(last).should.equal(expected)
            done()
        });
    })

    describe('Search page category', function () {

        var payload = {
            "pageType": "category",
            "url": "https://www.winfieldsoutdoors.co.uk/women/clothing/jackets-gilets/",
            "html": cache.load("https://www.winfieldsoutdoors.co.uk/women/clothing/jackets-gilets/")
        }

        it('should be true when the category is correct', function (done) {

            Structure(payload, function (err, category) {
                var expected = JSON.stringify({
                    "name": "Women's Outdoor Jackets, Coats & Gilets",
                    "url": payload.url 
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

});
