var should = require('should');
var Structure = require('../lib/structure.js')

describe('replaceWithwebAddress', function() {

    this.timeout(10000);

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "https://sport.woot.com/offers/columbia-mens-fleece?ref=w_cnt_lnd_cat_fshn_1_1"
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "Columbia Men's 1/4 Zip Fleece",
                    "price": "17.99",
                    "url": null,
                    "inStock": true,
                    "stockLevel": null,
                    "image": 'https://d3gqasl9vmjfd8.cloudfront.net/a99853fb-f3c0-4a7f-a1ba-371f7d296ed2.jpg',
                    "country": 'us',
                    "description": "A super-soft and hoodless 1/4 zip, this microfleece pullover is any man's everyday, all-around, go-to comfort fleece. Long sleeves Modern Classic Fit 1/4 Zip 100% polyester microfleece Imported Shipping Note: Shipping to Alaska, Hawaii, PO Boxes and APO addresses not available for this item Warranty: 90 Day Woot Limited Warranty"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    /*describe('Out of Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : ""
        }

        it('should be false when product is in store only', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "",
                    "price": "",
                    "url": null,
                    "inStock": false,
                    "stockLevel": null,
                    "image": '',
                    "country": '',
                    "description": ''
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });*/

    describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "http://www.woot.com/category/accessories/all?ref=w_cnt_cdet_fshn_1"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(pagination) {
                var expected = JSON.stringify({
                    "items": 24,
                    "pages": 19
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    })

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "http://www.woot.com/category/accessories/all?ref=w_cnt_cdet_fshn_1"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(category) {
                var expected = JSON.stringify({
                    "name": "Fashion"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "http://www.woot.com/category/accessories/all?ref=w_cnt_cdet_fshn_1"
        }

        // Change to be the number of products on the page
        var x = 24;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Under Armour 6" 5 Pack Boxer Jocks';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '39.99',
                    url: 'https://sport.woot.com/offers/under-armour-6-5-pack-boxer-jocks?ref=w_cnt_lnd_cat_fshn_1_1',
                    image: 'https://d3gqasl9vmjfd8.cloudfront.net/eceb1229-2f27-4a09-9147-0bd69626983b.jpg',
                    country: 'us'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'FILA Banded 3/4 Tight';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '15.99',
                    url: 'https://sellout.woot.com/offers/fila-banded-3-4-tight?ref=w_cnt_lnd_cat_fshn_1_24',
                    image: 'https://d3gqasl9vmjfd8.cloudfront.net/221a0b09-4d87-4a66-8952-b5989161ed61.jpg',
                    country: 'us'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});
