    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "https://www.worldkitchen.co.uk/Corelle/LivingWare/Garden-Lace",
            "html": cache.load("https://www.worldkitchen.co.uk/Corelle/LivingWare/Garden-Lace")
        }

        it('should be true when correct category is returned', function (done) {

            Structure(payload, function(err, category) {
                var expected = JSON.stringify({
                    "name": "Garden Lace",
                    "url": "https://www.worldkitchen.co.uk/Corelle/LivingWare/Garden-Lace"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "https://www.worldkitchen.co.uk/Visions/Flair",
            "html" : cache.load("https://www.worldkitchen.co.uk/Visions/Flair")
        }

        // Change to be the number of products on the page
        var x = 7;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(err, result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Visions Flair Cookpot 1.2L with Glass Cover - dia 15cm x h 13cm'
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(err, result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '53.95',
                    url: 'https://www.worldkitchen.co.uk/Visions/Flair/Visions-Flair-Cookpot-1.2L-with-Glass-Cover',
                    image: 'https://www.worldkitchen.co.uk/Resources/Resize_MiddleThumbimages/33382.jpg',
                    country: 'uk',
                    sku: 'Visions-Flair-Cookpot-1.2L-with-Glass-Cover'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Visions 6Pc Flair Casserole and Cookpot Pot Set - 1 x 1.6L , 2.8L and 3.8L Cookpot With Glass Covers';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(err, result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '178.95',
                    url: 'https://www.worldkitchen.co.uk/Visions/Flair/Visions-6Pc-Flair-Casserole-and-Cookpot-Pot-Set',
                    image: 'https://www.worldkitchen.co.uk/Resources/Resize_MiddleThumbimages/33396.jpg',
                    country: 'uk',
                    sku: 'Visions-6Pc-Flair-Casserole-and-Cookpot-Pot-Set'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

