var should = require('should');
var Structure = require('../lib/structure.js')

describe('www.worldstores.co.uk', function() {
    
    this.timeout(5000);   

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.worldstores.co.uk/p/Tutti_Bambini_3_Bears_Toy_Box.htm"
        }

        it('should be true when product is in stock', function (done) {
 
            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "Tutti Bambini 3 Bears Toy Box",
                    "price": "74.00",
                    "url": null,
                    "inStock": true,
                    "stockLevel": null,
                    "image": "https://images.static.worldstores.co/images/products/WP/7A/Tutti_Bambini_3_Bears_Toy_Box_1~9VZ.jpg",
                    "country": "uk"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            }) 
        });

    });

    describe('Out of Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.worldstores.co.uk/p/KidKraft_Austin_Toy_Box_in_Pink.htm"
        }

        it('should return false when product is out of stock', function(done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "KidKraft Austin Toy Box in Pink",
                    "price": "N/A",
                    "url": null,
                    "inStock": false,
                    "stockLevel": null,
                    "image": "https://images.static.worldstores.co/images/products/WP/3B/KidKraft_Austin_Toy_Box_in_Pink_2.jpg",
                    "country": "uk"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        })

    })

     describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "http://www.worldstores.co.uk/c/Garden_Storage.htm"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(pagination) {
                var expected = JSON.stringify({
                    "items": 54,
                    "pages": 4
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    });

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "http://www.worldstores.co.uk/c/Garden_Storage.htm"
        }

        it('should be true when the category is correct', function (done) {

            Structure(payload, function(category) {
                var expected = JSON.stringify({
                    "name": "Garden Storage"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "http://www.worldstores.co.uk/c/Garden_Storage.htm"
        }

        // Change to be the number of products on the page
        var x = 54;
        it('should return ' + x + ' number of products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Winchester Store Chest';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '146.99',
                    url: 'http://www.worldstores.co.uk/p/Winchester_4ft_x_3ft_Store_Chest.htm',
                    image: 'https://images.static.worldstores.co/images/products/SS/EA/Winchester_4ft_x_3ft__1_28m_x_0_77m__Store_Chest_3.jpg?i10c=img.resize(width:245,height:245)',
                    country: 'uk'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Rutland 4ft Double Maxi Log Store';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '197.99',
                    url: 'http://www.worldstores.co.uk/p/Rutland_4ft_Double_Maxi_Log_Store.htm',
                    image: 'https://images.static.worldstores.co/images/products/SS/1A/Rutland_4ft_Double_Maxi_Log_Store_A_SS_1.jpg?i10c=img.resize(width:245,height:245)',
                    country: 'uk'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });



});
