var should = require('should');
var Structure = require('../lib/structure.js')

describe('www.xtra-vision.co.uk', function() {
    
    this.timeout(5000);   

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.xtra-vision.co.uk/Fallout-4/xv-movies-games-entertainment/pd/118396?path=Category__Games__Xbox-One"
        }

        it('should be true when product is in stock', function (done) {
 
            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "Fallout 4 (Xbox One)",
                    "price": "54.99",
                    "url": null,
                    "inStock": true,
                    "stockLevel": null,
                    "image": "http://d3c8kntzevhldx.cloudfront.net/packshotuploads/118396.jpg",
                    "country": "uk"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Out of Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.xtra-vision.co.uk/Murdered-Soul-Suspect/xv-movies-games-entertainment/pd/103427?path=xbox-360"
        }

        it('should return false when product is out of stock', function(done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "Murdered: Soul Suspect (Xbox 360)",
                    "price": "22.99",
                    "url": null,
                    "inStock": false,
                    "stockLevel": null,
                    "image": "http://d3c8kntzevhldx.cloudfront.net/packshots/wsp101348488486175_l.jpg",
                    "country": "uk"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        })

    })
     describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "http://www.xtra-vision.co.uk/Movies-Highlights/cc/xv-movies-games-entertainment/Movies-Highlights/OrderBy/category/asc/Page/1/Size/20?path=Movies"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(pagination) {
                var expected = JSON.stringify({
                    "items": 20,
                    "pages": 23
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    });

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "http://www.xtra-vision.co.uk/Movies-Highlights/cc/xv-movies-games-entertainment/Movies-Highlights/OrderBy/category/asc/Page/1/Size/20?path=Movies"
        }

        it('should be true when the category is correct', function (done) {

            Structure(payload, function(category) {
                var expected = JSON.stringify({
                    "name":  "Movies Highlights"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "http://www.xtra-vision.co.uk/Movies-Highlights/cc/xv-movies-games-entertainment/Movies-Highlights/OrderBy/category/asc/Page/1/Size/20?path=Movies"
        }

        // Change to be the number of products on the page
        var x = 20;
        it('should return ' + x + ' number of products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName =  'Planet Earth II';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '16.43',
                    url: 'http://www.xtra-vision.co.uk/Planet-Earth-II/xv-movies-games-entertainment/pd/1072375?path=dvd',
                    image: 'http://d3c8kntzevhldx.cloudfront.net/packshots/WMM00229736_M.JPG',
                    country: 'uk'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Ice Age: Collision Course';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '16.93',
                    url: 'http://www.xtra-vision.co.uk/Ice-Age-Collision-Course/xv-movies-games-entertainment/pd/1057886?path=dvd',
                    image: 'http://d3c8kntzevhldx.cloudfront.net/packshots/WMM00227118_M.JPG',
                    country: 'uk'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });


});
