var should = require('should');
var Structure = require('../lib/structure.js')

describe('www.yoyo.com', function() {

    this.timeout(10000);

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "https://www.yoyo.com/p/kidsconnect-white-phone-1239273?sku=TZE-001&qid=3251647781&sr=1-2"
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "KidsConnect White Phone",
                    "price": "62.99",
                    "url": null,
                    "inStock": true,
                    "stockLevel": null,
                    "image": 'https://c1.q-assets.com/images/products/p/tze/tze-001_1.jpg',
                    "country": 'us'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Out of Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "https://www.yoyo.com/p/pink-cookie-overhead-stereo-headphones-926384?sku=VRY-026&qid=3525025563&sr=1-1"
        }

        it('should be false when product is in store only', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "Pink Cookie Overhead Stereo Headphones",
                    "price": "14.00",
                    "url": null,
                    "inStock": false,
                    "stockLevel": null,
                    "image": 'https://c4.q-assets.com/images/products/p/vry/vry-026_1.jpg',
                    "country": 'us'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "https://www.yoyo.com/subcat=Action-Figures-10499?pg=2&ref=y_bro_tclc1_qbnc"
        }

        // Change to be the number of products on the page
        var x = 60;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Superman Action Figure, 31 Inches';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '39.63',
                    url: 'https://www.yoyo.com/p/batman-vs-superman-superman-action-figure-31-inches-1280357?sku=WS-1538&qid=3393365589&sr=1-61',
                    image: 'https://c3.q-assets.com/images/products/WS/WS-1538_med5a.jpg',
                    country: 'us'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'BattleBot REMOTE COMBAT single';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '29.99',
                    url: 'https://www.yoyo.com/p/hexbug-battlebot-remote-combat-single-1350298?sku=XTP-148&qid=3393365589&sr=1-120',
                    image: 'https://c3.q-assets.com/images/products/XTP/XTP-148_med5a.jpg',
                    country: 'us'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

    describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "https://www.yoyo.com/cat=Video-Games-Electronics-14?icn=YO-nav-Category&ici=VideoGamesElectronics"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(pagination) {
                var expected = JSON.stringify({
                    "items": 60,
                    "pages": 4
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    });

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "https://www.yoyo.com/cat=Video-Games-Electronics-14?icn=YO-nav-Category&ici=VideoGamesElectronics"
        }

        it('should be true when the category is correct', function (done) {

            Structure(payload, function(category) {
                var expected = JSON.stringify({
                    "name": "Video Games & Electronics"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

});
