var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')

describe('replaceWithwebAddress', function() {

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "https://www.bitsandpieces.com/product/money-maze-bank",
            "html": cache.load("https://www.bitsandpieces.com/product/money-maze-bank")
        }

        it('should be true when product is in stock', function () {
            
           const inStockPage =  Structure(payload)            
           inStockPage.name.should.be.equal("Black Diamond HeavyWeight Waterproof Gloves");
           inStockPage.price.should.be.equal("14.99");
           inStockPage.url.should.be.equal("https://www.bitsandpieces.com/product/money-maze-bank");
           inStockPage.inStock.should.be.equal(true);
           should(inStockPage.stockLevel).be.equal(null);
           inStockPage.inStock.should.be.equal(true);
           inStockPage.image.should.be.equal('https://s1.thcdn.com/productimg/1600/1600/11580543-1104537722369471.jpg');
           inStockPage.country.should.be.equal('us');            
        });

    });

    describe('Out of Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.zavvi.com/merch-figures/walking-dead-bloody-biker-daryl-previews-pop-vinyl-figure/11333333.html?switchcurrency=GBP&shippingcountry=GB&affil=awin&awc=2549_1472665887_4a9dbc176cfb99d5a1c8387b0d719016&utm_source=AWin-216555&utm_medium=affiliate&utm_campaign=AffiliateWin"
        }

        it('should be false when product is no longer available', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "Walking Dead Bloody Biker Daryl Previews! Pop! Vinyl Figure",
                    "price": "9.99",
                    "url": null,
                    "inStock": false,
                    "stockLevel": null,
                    "image": 'http://s1.thcdn.com/productimg/300/300/11333333-1494417724488644.jpg',
                    "country": 'uk'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Pre-order Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "https://www.zavvi.com/games-ps4/dragon-crown-pro/11664205.html"
        }

        it('should return false when product is available for pre order', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "Dragon Crown Pro PS4",
                    "price": "44.99",
                    "url": 'https://www.zavvi.com/games-ps4/dragon-crown-pro/11664205.html',
                    "inStock": false,
                    "stockLevel": null,
                    "image": "https://s2.thcdn.com/productimg/600/600/11664205-6974547289283575.jpg",
                    "country": "uk",
                    "sku": '11664205'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "http://www.zavvi.com/dvd/offers/3-for-20.list"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(pagination) {
                var expected = JSON.stringify({
                    "items": 64,
                    "pages": 7
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    });

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "http://www.zavvi.com/games/latest-releases.list"
        }

        it('should be true when the category is correct', function (done) {

            Structure(payload, function(category) {
                var expected = JSON.stringify({
                    "name": "Latest Releases"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });


    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "https://www.zavvi.com/offers/christmas/gifts-for-him/memorabilia.list",
            "html": cache.load("https://www.zavvi.com/offers/christmas/gifts-for-him/memorabilia.list")

        }

        // Change to be the number of products on the page
        var x = 7;
        it('should return ' + x + ' numer of products', function () {
            let result = Structure(payload)
            result.length.should.equal(x);
        });

        var firstProductName = 'Critters Go For A Ride 100 Large Piece Jigsaw Puzzle';
        it('first product should be ' + firstProductName, function() {

            var expected = JSON.stringify({
                barcode: null,
                name: firstProductName,
                price: '4.99',
                url: 'http://www.bitsandpieces.com/product/critters-go-for-a-ride-100-large-piece-jigsaw-puzzle-42777',
                image: 'https://s3.amazonaws.com/cdn.bitsandpieces.com/images/160/42777.jpg',
                country: 'us'
            })
            
            let result = Structure(payload)

            var first = result.shift()
            
            JSON.stringify(first).should.equal(expected)

        })

        var lastProductName = 'Soccer Sliding Wooden Puzzle';
        it('last product should be ' + lastProductName, function () {
            var expected = JSON.stringify({
                barcode: null,
                name: lastProductName,
                price: '14.99',
                url: 'http://www.bitsandpieces.com/product/soccer-sliding-wooden-puzzle',
                image: 'https://s3.amazonaws.com/cdn.bitsandpieces.com/images/160/43952.jpg',
                country: 'us'
            })

            const result = Structure(payload);

            var last = result.pop()
            JSON.stringify(last).should.equal(expected)
        })

    }); 

});
