describe('Search Page', function () {

    var payload = {
        "pageType": "search",
        "url": "https://www.zooplus.co.uk/shop/dogs/dry_dog_food/royal_canin_vet_diet?utm_source=gridclick&utm_medium=dog&utm_campaign=royal_canin_vet_pic&utm_content=image&utm_term=dog_dry",
        "html": cache.load("https://www.zooplus.co.uk/shop/dogs/dry_dog_food/royal_canin_vet_diet?utm_source=gridclick&utm_medium=dog&utm_campaign=royal_canin_vet_pic&utm_content=image&utm_term=dog_dry")
    }

    // Change to be the number of products on the page
    var x = 27;
    it('should return ' + x + ' number of products', function (done) {
        Structure(payload, function (err, result) {
            result.length.should.equal(x);
            done()
        })
    });

    var firstProductName = 'Royal Canin Veterinary Diet Dog - Hypoallergenic DR 21';
    it('first product should be ' + firstProductName, function (done) {
        Structure(payload, function (err, result) {
            var first = result.shift()
            var expected = JSON.stringify({
                barcode: null,
                name: firstProductName,
                price: null,
                url: 'https://www.zooplus.co.uk/shop/dogs/dry_dog_food/royal_canin_vet_diet/307309',
                image: 'https://shop-cdn-m.shpp.ext.zooplus.io/bilder/royal/canin/veterinary/diet/dog/hypoallergenic/dr/5/140/52631_PLA_Royal_Canin_Vet_Hypoallergenic_5_5.jpg',
                country: 'uk'
            })
            JSON.stringify(first).should.equal(expected)
            done()
        })

    })

    var lastProductName = 'Royal Canin Veterinary Diet Dog - Calm CD 25';
    it('last product should be ' + lastProductName, function (done) {
        Structure(payload, function (err, result) {
            var last = result.pop()
            var expected = JSON.stringify({
                barcode: null,
                name: lastProductName,
                price: null,
                url: 'https://www.zooplus.co.uk/shop/dogs/dry_dog_food/royal_canin_vet_diet/356767',
                image: 'https://shop-cdn-m.shpp.ext.zooplus.io/bilder/royal/canin/veterinary/diet/dog/calm/cd/5/140/royal_canin_vet_dog_calm_5.jpg',
                country: 'uk'
            })

            JSON.stringify(last).should.equal(expected)
            done()
        });
    })

    describe('Search page category', function () {

        var payload = {
            "pageType": "category",
            "url": "https://www.zooplus.co.uk/shop/dogs/dry_dog_food/royal_canin_vet_diet?utm_source=gridclick&utm_medium=dog&utm_campaign=royal_canin_vet_pic&utm_content=image&utm_term=dog_dry",
            "html": cache.load("https://www.zooplus.co.uk/shop/dogs/dry_dog_food/royal_canin_vet_diet?utm_source=gridclick&utm_medium=dog&utm_campaign=royal_canin_vet_pic&utm_content=image&utm_term=dog_dry")
        }

        it('should be true when the category is correct', function (done) {

            Structure(payload, function (err, category) {
                var expected = JSON.stringify({
                    "name": "Royal Canin Veterinary Diet",
                    "url": payload.url 
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

});
