var should = require('should');
var Structure = require('../lib/structure.js')

describe('replaceWithwebAddress', function() {

    this.timeout(10000);

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.zulily.com/p/blue-plaid-romper-infant-214320-37019147.html?search_pos=1&search_page=1&fromSearch=true&"
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "Blue Plaid Romper - Infant",
                    "price": "14.99",
                    "url": null,
                    "inStock": true,
                    "stockLevel": null,
                    "image": 'http://mcdn.zulilyinc.com/media/catalog/product//170677/zu37019147_main_tm1458840675.jpg',
                    "country": 'us',
                    "description": 'Your little one will be comfy as can be in this soft romper complete with a button-up placket to make diaper changes in a snap.100% cottonMachine washImportedShipping note: This item is shipping internationally. Allow extra time for its journey to you.'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    /*describe('Out of Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : ""
        }

        it('should be false when product is in store only', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "",
                    "price": "",
                    "url": null,
                    "inStock": false,
                    "stockLevel": null,
                    "image": '',
                    "country": '',
                    "description": ''
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });*/

    describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "http://www.zulily.com/cotton?fromSearch=true&searchTerm=cotton&page=2"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(pagination) {
                var expected = JSON.stringify({
                    "items": 84,
                    "pages": 296
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    })

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "http://www.zulily.com/cotton?fromSearch=true&searchTerm=cotton&page=2"
        }

        // Change to be the number of products on the page
        var x = 84;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = "'Happy Place' Cotton Dish Towel";
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '6.99',
                    url: 'http://www.zulily.com/p/happy-place-cotton-dish-towel-201135-40097206.html?search_pos=1&search_page=2&fromSearch=true&',
                    image: 'http://mcdn.zulilyinc.com/images/cache/product/318x1000//195032/zu40097206_main_tm1467307527.jpg',
                    country: 'us'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Light Blue Dandy Pima Cotton Tee - Infant';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '10.99',
                    url: 'http://www.zulily.com/p/light-blue-dandy-pima-cotton-tee-infant-214975-40102805.html?search_pos=84&search_page=2&fromSearch=true&',
                    image: 'http://mcdn.zulilyinc.com/images/cache/product/318x1000//202093/zu40102805_main_tm1470245938.jpg',
                    country: 'us'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });
    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "http://www.zulily.com/cotton?fromSearch=true&searchTerm=cotton&page=2"
        }

        it('should be true when the category is correct', function (done) {

            Structure(payload, function(category) {
                var expected = JSON.stringify({
                    "name": "cotton"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

});
