const CacheStore = require('./cacheStore')

class AmazonIdToSupplier extends CacheStore {

    constructor(config) {
        super(config, "AMAZONIDTOCAT")
    }

    setCategory(country, id, name) {
        this.set(country + ":" + id, "NAME", name)
    }

    getName(country, id, next) {        
        this.get(country + ":" + id, "NAME", next)
    }

}

module.exports = AmazonIdToSupplier
