const CacheStore = require('./cacheStore')

class DomainToId extends CacheStore {

    constructor(config) {
        super(config, "DOMAINTOID")
    }

    setSupplier(country, domain, id) {
        this.set(country + ":" + domain, "ID", id)
    }

    getId(country, domain, next) {
        this.get(country + ":" + domain, "ID", next)
    }

}

module.exports = DomainToId
