const CacheStore = require('./cacheStore')

class IdToSupplier extends CacheStore {

    constructor(config) {
        super(config, "IDTOSUPPLIER")
    }

    setSupplier(country, id, domain, name) {
        this.set(country + ":" + id, "DOMAIN", domain)
        this.set(country + ":" + id, "NAME", name)
    }

    getName(country, id, next) {
        this.get(country + ":" + id, "NAME", next)
    }

    getDomain(country, id, next) {
        this.get(country + ":" + id, "DOMAIN", next)
    }

    getLiveSuppliers(country, next) {
        this.getKeys(this.type + ":" + country, next)
    }
    
    getAllInfo(country, id, next) {
        this.getAll(country + ":" + id, next)
    }

}

module.exports = IdToSupplier
