module.exports = (payload) => {
    return {
        "sort": [
            {
                "updated": {
                    "order": "desc"
                }
            }
        ],
        "from": payload.from,
        "size": payload.size,
        "query": {
            "bool": {
                "must": [
                    {
                        "exists": {
                            "field": "barcode"
                        }
                    },
                    {
                        "match": {
                            "state": "new"
                        }
                    },
                    {
                        "match": {
                            "isValidBarcode": "true"
                        }
                    }
                ],
                "must_not": [
                    {
                        "exists": {
                            "field": "asin"
                        }
                    }
                ]
            }
        }
    };
};
