const handle = require('./handle')

class Mongo {

    constructor(config) {
        this.config = config;
    }

    conn(table, next) {
        handle(this.config, (err, db) => {
            if (err) {
                console.log("BAD")
                next(err);
            } else {
                var collection = db.collection(table);
                next(null, collection)
            }
        });
    }

}

module.exports = Mongo;