const Mongo = require('../index').mongo

let MDB = new Mongo({
    db: 'sm',
    host: "127.0.0.1",
    port: 27017
})

MDB.conn("ussuppliers", (err, collection) => {
    if (err) {
        console.error(err)
    } else {
        collection.find({ name: { $regex: /Walmart cat/ } }, { _id: 1, name: 1 }).sort({ name: 1 })
            .toArray((err, data) => {
                if(err) {
                    console.error(err)
                } else {
                    let ids = []
                    data.map((d) => {
                        ids.push(d._id.toString())
                    })
                    console.log(ids)
                }
                
            })
    }

})

