const should = require('should')
const makeSafe = require('../lib/makeSafe');

describe('makeSafe Q Name', () => {    

    it('should remove spaces and quotes', () => {
        let qname = 'Toy1``s Rus"\''
        makeSafe(qname).should.be.equal('Toy1--s-Rus--')
    })

    it('should remove colons and dots', () => {
        let qname = 'Toy:s.Rus'
        makeSafe(qname).should.be.equal('Toy-s-Rus')
    })

    it('should remove commas and apersands', () => {
        let qname = 'Toy,s&Rus'
        makeSafe(qname).should.be.equal('Toy-s-Rus')
    })

    it('should remove brakets ', () => {
        let qname = 'Toy{}s()Rus[-]'
        makeSafe(qname).should.be.equal('Toy--s--Rus---')
    })

})
