const should = require('should')
const Q = require('../index').q;

const config = {
    host: '127.0.0.1',
    port: 6379,
    username: '',
    passwd:  ''
}

xdescribe('qGetNTest No Messages', function() {    

    this.timeout(10000)

    it('should return only 2 messages when there are two messages', (done) => {
        config.options = {
            interval: 100,
            maxWait: 1000 
        }
        const testQ = new Q(config, 'testGetN');
        
        testQ.send('MESSAGE_TYPE', {
            waitTest2: 'two'
        }, (err, res) => {
        })

        setTimeout(() => {            
            testQ.worker((err, msg, finish) => {
                if(err) {
                    done(err)
                    return
                }
                finish()            
                testQ.getN(4, (err, messages) => {
                    should(err).be.equal(null)
                    messages.length.should.be.equal(0)
                    done()
                })
    
            })
        }, 500)


    })

})
