module.exports = (message) => {

    let jobId = message.userName + '-' + message.jobName + '_' + message.jobType + '-' + message.country + '-' + message.date;

    if(message.jobType === 'reverseSearch') {
        jobId = message.userName + '-' + message.jobName + '-' + message.country + '-' + message.date;
    }

    return jobId
}

