var MongoClient = require('mongodb').MongoClient;

module.exports = function (config, next) {
	var url = 'mongodb://' + config.host + ':' + config.port + '/' + config.db;
	MongoClient.connect(url, {
		reconnectTries: 3600,
		reconnectInterval: 1000,
		autoReconnect: true
	}, function (err, db) {
		next(err, db);
	});
};
