const Base = require('../lib/mongo/base')
const md5 = require('md5')
const mongodb = require('mongodb')

class Html extends Base {

    constructor(config) {
        super(config, 'html')
    }
    
    findByUrl(url, done) {
        var id = md5(url)
        
        this.find({
            _id: id
        }, (err, result) => {
            done(err, result)
        })
    }

}

module.exports = Html